mapper.select:
/* DaySaleStoreMomsMapper.getDaySaleStoreMomsList */
SELECT TO_CHAR(TO_DATE(tsdtd.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE, tsdtd.STORE_CD, tms.STORE_NM, tsdtd.TOT_SALE_AMT, tsdtd.TOT_REAL_SALE_AMT, tsdtd.TOT_BILL_CNT, tsdtd.STIN_TOT_SALE_AMT, tsdtd.STIN_REAL_SALE_AMT, tsdtd.STIN_BILL_CNT, tsdtd.DLVR_TOT_SALE_AMT, tsdtd.DLVR_REAL_SALE_AMT, tsdtd.DLVR_BILL_CNT, tsdtd.PACK_TOT_SALE_AMT, tsdtd.PACK_REAL_SALE_AMT, tsdtd.PACK_BILL_CNT, tsdto.TOT_SALE_AMT3 AS BAEMIN_TOT_SALE_AMT, tsdto.REAL_SALE_AMT3 AS BAEMIN_REAL_SALE_AMT, tsdto.BILL_CNT3 AS BAEMIN_BILL_CNT, tsdto.TOT_SALE_AMT4 AS YOGIYO_TOT_SALE_AMT, tsdto.REAL_SALE_AMT4 AS YOGIYO_REAL_SALE_AMT, tsdto.BILL_CNT4 AS YOGIYO_BILL_CNT, tsdto.TOT_SALE_AMT5 AS COUPANGEATS_TOT_SALE_AMT, tsdto.REAL_SALE_AMT5 AS COUPANGEATS_REAL_SALE_AMT, tsdto.BILL_CNT5 AS COUPANGEATS_BILL_CNT, tsdto.TOT_SALE_AMT0 AS ETC_TOT_SALE_AMT, tsdto.REAL_SALE_AMT0 AS REAL_SALE_AMT, tsdto.BILL_CNT0 AS ETC_BILL_CNT
FROM ( SELECT tsdtd.SALE_DATE, tsdtd.STORE_CD, SUM(tsdtd.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsdtd.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT, SUM(tsdtd.REAL_SALE_CNT) AS TOT_BILL_CNT, SUM(CASE WHEN tsdtd.DLVR_ORDER_FG = '1' THEN tsdtd.TOT_SALE_AMT ELSE 0 END) AS STIN_TOT_SALE_AMT, SUM(CASE WHEN tsdtd.DLVR_ORDER_FG = '1' THEN tsdtd.REAL_SALE_AMT ELSE 0 END) AS STIN_REAL_SALE_AMT, SUM(CASE WHEN tsdtd.DLVR_ORDER_FG = '1' THEN tsdtd.REAL_SALE_CNT ELSE 0 END) AS STIN_BILL_CNT, SUM(CASE WHEN tsdtd.DLVR_ORDER_FG = '2' THEN tsdtd.TOT_SALE_AMT ELSE 0 END) AS DLVR_TOT_SALE_AMT, SUM(CASE WHEN tsdtd.DLVR_ORDER_FG = '2' THEN tsdtd.REAL_SALE_AMT ELSE 0 END) AS DLVR_REAL_SALE_AMT, SUM(CASE WHEN tsdtd.DLVR_ORDER_FG = '2' THEN tsdtd.REAL_SALE_CNT ELSE 0 END) AS DLVR_BILL_CNT, SUM(CASE WHEN (tsdtd.DLVR_ORDER_FG = '3' OR tsdtd.DLVR_ORDER_FG = '4') THEN tsdtd.TOT_SALE_AMT ELSE 0 END) AS PACK_TOT_SALE_AMT, SUM(CASE WHEN (tsdtd.DLVR_ORDER_FG = '3' OR tsdtd.DLVR_ORDER_FG = '4') THEN tsdtd.REAL_SALE_AMT ELSE 0 END) AS PACK_REAL_SALE_AMT, SUM(CASE WHEN (tsdtd.DLVR_ORDER_FG = '3' OR tsdtd.DLVR_ORDER_FG = '4') THEN tsdtd.REAL_SALE_CNT ELSE 0 END) AS PACK_BILL_CNT FROM TB_SL_DAILY_TOTAL_DLVR tsdtd WHERE 1=1 AND tsdtd.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdtd.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdtd.SALE_DATE, tsdtd.STORE_CD ) tsdtd, ( SELECT tsdto.SALE_DATE, tsdto.STORE_CD, SUM(CASE WHEN tsdto.DLVR_IN_FG = '3' THEN tsdto.TOT_SALE_AMT ELSE 0 END) AS TOT_SALE_AMT3, SUM(CASE WHEN tsdto.DLVR_IN_FG = '3' THEN tsdto.REAL_SALE_AMT ELSE 0 END) AS REAL_SALE_AMT3, SUM(CASE WHEN tsdto.DLVR_IN_FG = '3' THEN tsdto.SALE_CNT ELSE 0 END) AS BILL_CNT3, SUM(CASE WHEN tsdto.DLVR_IN_FG = '4' THEN tsdto.TOT_SALE_AMT ELSE 0 END) AS TOT_SALE_AMT4, SUM(CASE WHEN tsdto.DLVR_IN_FG = '4' THEN tsdto.REAL_SALE_AMT ELSE 0 END) AS REAL_SALE_AMT4, SUM(CASE WHEN tsdto.DLVR_IN_FG = '4' THEN tsdto.SALE_CNT ELSE 0 END) AS BILL_CNT4, SUM(CASE WHEN tsdto.DLVR_IN_FG = '5' THEN tsdto.TOT_SALE_AMT ELSE 0 END) AS TOT_SALE_AMT5, SUM(CASE WHEN tsdto.DLVR_IN_FG = '5' THEN tsdto.REAL_SALE_AMT ELSE 0 END) AS REAL_SALE_AMT5, SUM(CASE WHEN tsdto.DLVR_IN_FG = '5' THEN tsdto.SALE_CNT ELSE 0 END) AS BILL_CNT5, SUM(CASE WHEN tsdto.DLVR_IN_FG NOT IN ('3', '4', '5') THEN tsdto.TOT_SALE_AMT ELSE 0 END) AS TOT_SALE_AMT0, SUM(CASE WHEN tsdto.DLVR_IN_FG NOT IN ('3', '4', '5') THEN tsdto.REAL_SALE_AMT ELSE 0 END) AS REAL_SALE_AMT0, SUM(CASE WHEN tsdto.DLVR_IN_FG NOT IN ('3', '4', '5') THEN tsdto.SALE_CNT ELSE 0 END) AS BILL_CNT0 FROM TB_SL_DAILY_TOTAL_ORDAPP tsdto WHERE 1=1 AND tsdto.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdto.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdto.SALE_DATE, tsdto.STORE_CD ) tsdto, TB_MS_STORE tms, TB_MS_STORE_INFO tmsi WHERE 1=1
AND tsdtd.STORE_CD IN (${storeCdQuery})
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}
AND tsdto.SALE_DATE = tsdtd.SALE_DATE AND tsdto.STORE_CD = tsdtd.STORE_CD AND tms.STORE_CD = tsdtd.STORE_CD AND tmsi.STORE_CD (+)= tms.STORE_CD ORDER BY tsdtd.SALE_DATE, tsdtd.STORE_CD

