LVR_ORDER_FG = '3' OR tsdtd.DLVR_ORDER_FG = '4') THEN tsdtd.REAL_SALE_CNT ELSE 0 END) AS PACK_BILL_CNT FROM TB_SL_DAILY_TOTAL_DLVR tsdtd WHERE 1=1 AND tsdtd.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdtd.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdtd.SALE_DATE, tsdtd.STORE_CD ) tsdtd, ( SELECT tsdto.SALE_DATE, tsdto.STORE_CD, SUM(CASE WHEN tsdto.DLVR_IN_FG = '3' THEN tsdto.TOT_SALE_AMT ELSE 0 END) AS TOT_SALE_AMT3, SUM(CASE WHEN tsdto.DLVR_IN_FG = '3' THEN tsdto.REAL_SALE_AMT ELSE 0 END) AS REAL_SALE_AMT3, SUM(CASE WHEN tsdto.DLVR_IN_FG = '3' THEN tsdto.SALE_CNT ELSE 0 END) AS BILL_CNT3, SUM(CASE WHEN tsdto.DLVR_IN_FG = '4' THEN tsdto.TOT_SALE_AMT ELSE 0 END) AS TOT_SALE_AMT4, SUM(CASE WHEN tsdto.DLVR_IN_FG = '4' THEN tsdto.REAL_SALE_AMT ELSE 0 END) AS REAL_SALE_AMT4, SUM(CASE WHEN tsdto.DLVR_IN_FG = '4' THEN tsdto.SALE_CNT ELSE 0 END) AS BILL_CNT4, SUM(CASE WHEN tsdto.DLVR_IN_FG = '5' THEN tsdto.TOT_SALE_AMT ELSE 0 END) AS TOT_SALE_AMT5, SUM(CASE WHEN tsdto.DLVR_IN_FG = '5' THEN tsdto.REAL_SALE_AMT ELSE 0 END) AS REAL_SALE_AMT5, SUM(CASE WHEN tsdto.DLVR_IN_FG = '5' THEN tsdto.SALE_CNT ELSE 0 END) AS BILL_CNT5, SUM(CASE WHEN tsdto.DLVR_IN_FG NOT IN ('3', '4', '5') THEN tsdto.TOT_SALE_AMT ELSE 0 END) AS TOT_SALE_AMT0, SUM(CASE WHEN tsdto.DLVR_IN_FG NOT IN ('3', '4', '5') THEN tsdto.REAL_SALE_AMT ELSE 0 END) AS REAL_SALE_AMT0, SUM(CASE WHEN tsdto.DLVR_IN_FG NOT IN ('3', '4', '5') THEN tsdto.SALE_CNT ELSE 0 END) AS BILL_CNT0 FROM TB_SL_DAILY_TOTAL_ORDAPP tsdto WHERE 1=1 AND tsdto.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdto.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsdto.SALE_DATE, tsdto.STORE_CD ) tsdto, TB_MS_STORE tms, TB_MS_STORE_INFO tmsi WHERE 1=1
AND tsdtd.STORE_CD IN (${storeCdQuery})
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
