mapper.select:
/* ProdSalePmixMomsMapper.getProdSalePmixMomsList */
SELECT PROD_CLASS_NM, PROD_NM ${sQuery2}
FROM ( SELECT FN_GET_PROD_CLASS_CD_NM_STR(tsdp.STORE_CD, tmp.PROD_CLASS_CD, 'NM_STR') AS PROD_CLASS_NM, tmp.PROD_NM ${sQuery1} FROM (
SELECT A.SALE_DATE, A.STORE_CD, A.PROD_CD, SUM(A.TOT_SALE_QTY) AS SALE_QTY, SUM(A.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_PROD A WHERE 1=1 AND A.HQ_OFFICE_CD = #{hqOfficeCd} AND A.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND A.STORE_CD IN (${storeCdQuery})
GROUP BY A.SALE_DATE, A.STORE_CD, A.PROD_CD
SELECT A.SALE_YM AS SALE_DATE, A.STORE_CD, A.PROD_CD, SUM(A.TOT_SALE_QTY) AS SALE_QTY, SUM(A.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_MONTHLY_PROD A WHERE 1=1 AND A.HQ_OFFICE_CD = #{hqOfficeCd} AND A.SALE_YM BETWEEN #{startDate} AND #{endDate}
AND A.STORE_CD IN (${storeCdQuery})
GROUP BY A.SALE_YM, A.STORE_CD, A.PROD_CD
) tsdp, (
SELECT A.SALE_DATE, A.STORE_CD, SUM(A.REAL_SALE_AMT) AS P_REAL_SALE_AMT FROM TB_SL_DAILY_PROD A WHERE 1=1 AND A.HQ_OFFICE_CD = #{hqOfficeCd} AND A.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND A.STORE_CD IN (${storeCdQuery})
GROUP BY A.SALE_DATE, A.STORE_CD
SELECT A.SALE_YM AS SALE_DATE, A.STORE_CD, SUM(A.REAL_SALE_AMT) AS P_REAL_SALE_AMT FROM TB_SL_MONTHLY_PROD A WHERE 1=1 AND A.HQ_OFFICE_CD = #{hqOfficeCd} AND A.SALE_YM BETWEEN #{startDate} AND #{endDate}
AND A.STORE_CD IN (${storeCdQuery})
GROUP BY A.SALE_YM, A.STORE_CD
) tsdp2, TB_MS_STORE tms, TB_MS_STORE_INFO tmsi, TB_MS_PRODUCT tmp WHERE 1=1
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = tmp.STORE_CD START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = tmp.STORE_CD CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = tmp.STORE_CD )
AND tmp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND tmp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND
tmp.PROD_CD = #{item}
AND tmp.HQ_BRAND_CD = #{prodHqBrandCd} -- 상품브랜드 선택일때
-- 상품브랜드 전체일때 AND tmp.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}
AND tsdp2.SALE_DATE = tsdp.SALE_DATE AND tsdp2.STORE_CD = tsdp.STORE_CD AND tms.STORE_CD = tsdp.STORE_CD AND tmsi.STORE_CD (+)= tms.STORE_CD AND tmp.STORE_CD = tsdp.STORE_CD AND tmp.PROD_CD = tsdp.PROD_CD GROUP BY tsdp.STORE_CD, tmp.PROD_CLASS_CD, tmp.PROD_NM ) GROUP BY PROD_CLASS_NM, PROD_NM ORDER BY PROD_CLASS_NM, PROD_NM

