/* TimeProdSalePmixMomsMapper.getTimeProdSalePmixMomsList */
SELECT T1.PROD_CLASS_CD, FN_GET_PROD_CLASS_CD_NM_STR('000003', T1.PROD_CLASS_CD, 'NM_STR') AS PROD_CLASS_NM, --속도를 위해 분류 매장코드 고정 T1.PROD_CD, T1.PROD_NM ${sQuery2}
FROM ( SELECT B.PROD_CLASS_CD, B.PROD_CD, B.PROD_NM ${sQuery1} FROM ( SELECT A.SALE_DATE, A.PROD_CLASS_CD, A.PROD_CD, A.PROD_NM, A.SALE_QTY1, A.REAL_SALE_AMT1, SUM(A.REAL_SALE_AMT1) OVER(PARTITION BY A.SALE_DATE) AS P_REAL_SALE_AMT1, SUM(A.REAL_SALE_AMT1) OVER() AS TOT_REAL_SALE_AMT1 FROM ( SELECT
tsdps.SALE_DATE,
tsdps.SALE_YM AS SALE_DATE,
tmp.PROD_CLASS_CD, tsdps.PROD_CD, tmp.PROD_NM, SUM(tsdps.TOT_SALE_QTY) AS SALE_QTY1, SUM(tsdps.REAL_SALE_AMT) AS REAL_SALE_AMT1 FROM
TB_SL_DAILY_PROD_SDSEL_MOMS_V04 tsdps,
TB_SL_MONTHLY_PROD_SDSEL_MOMS_V04 tsdps,
TB_MS_STORE tms, TB_MS_STORE_INFO tmsi, TB_MS_PRODUCT tmp WHERE 1=1 AND tsdps.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdps.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdps.SALE_YM BETWEEN #{startDate} AND #{endDate}
AND tsdps.STORE_CD IN (${storeCdQuery})
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = tmp.STORE_CD START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = tmp.STORE_CD CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = tmp.STORE_CD )
AND tmp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND tmp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND tmp.PROD_CD IN (${prodCdQuery})
AND tmp.HQ_BRAND_CD = #{prodHqBrandCd} -- 상품브랜드 선택일때
-- 상품브랜드 전체일때 AND tmp.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}
AND tmsi.MOMS_STORE_FG_02 = #{momsStoreFg02}
AND tmsi.MOMS_STORE_FG_03 = #{momsStoreFg03}
AND tmsi.MOMS_STORE_FG_04 = #{momsStoreFg04}
AND tmsi.MOMS_STORE_FG_05 = #{momsStoreFg05}
AND tms.STORE_CD = tsdps.STORE_CD AND tmsi.STORE_CD (+)= tms.STORE_CD AND tmp.STORE_CD = tsdps.STORE_CD AND tmp.PROD_CD = tsdps.PROD_CD
GROUP BY tsdps.SALE_DATE, tmp.PROD_CLASS_CD, tsdps.PROD_CD, tmp.PROD_NM
GROUP BY tsdps.SALE_YM, tmp.PROD_CLASS_CD, tsdps.PROD_CD, tmp.PROD_NM
) A GROUP BY A.SALE_DATE, A.PROD_CLASS_CD, A.PROD_CD, A.PROD_NM, A.SALE_QTY1, A.REAL_SALE_AMT1 ) B GROUP BY B.PROD_CLASS_CD, B.PROD_CD, B.PROD_NM ORDER BY B.PROD_CLASS_CD, B.PROD_CD ) T1, ( SELECT B.PROD_CLASS_CD, B.PROD_CD, B.PROD_NM ${sQuery3} FROM ( SELECT A.SALE_HOUR, A.PROD_CLASS_CD, A.PROD_CD, A.PROD_NM, A.TIME_SALE_QTY1, A.TIME_REAL_SALE_AMT1, SUM(A.TIME_REAL_SALE_AMT1) OVER(PARTITION BY A.SALE_HOUR) AS TIME_P_REAL_SALE_AMT1 FROM ( SELECT tsdpst.SALE_HOUR, tmp.PROD_CLASS_CD, tsdpst.PROD_CD, tmp.PROD_NM, SUM(tsdpst.TOT_SALE_QTY) AS TIME_SALE_QTY1, SUM(tsdpst.REAL_SALE_AMT) AS TIME_REAL_SALE_AMT1 FROM
TB_SL_DAILY_PROD_SDSEL_TIME_MOMS_V04 tsdpst,
TB_SL_MONTHLY_PROD_SDSEL_TIME_MOMS_V04 tsdpst,
TB_MS_STORE tms, TB_MS_STORE_INFO tmsi, TB_MS_PRODUCT tmp WHERE 1=1 AND tsdpst.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdpst.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdpst.SALE_YM BETWEEN #{startDate} AND #{endDate}
AND tsdpst.STORE_CD IN (${storeCdQuery})
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = tmp.STORE_CD START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = tmp.STORE_CD CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = tmp.STORE_CD )
AND tmp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND tmp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND tmp.PROD_CD IN (${prodCdQuery})
AND tmp.HQ_BRAND_CD = #{prodHqBrandCd} -- 상품브랜드 선택일때
-- 상품브랜드 전체일때 AND tmp.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}
AND tmsi.MOMS_STORE_FG_02 = #{momsStoreFg02}
AND tmsi.MOMS_STORE_FG_03 = #{momsStoreFg03}
AND tmsi.MOMS_STORE_FG_04 = #{momsStoreFg04}
AND tmsi.MOMS_STORE_FG_05 = #{momsStoreFg05}
AND tms.STORE_CD = tsdpst.STORE_CD AND tmsi.STORE_CD (+)= tms.STORE_CD AND tmp.STORE_CD = tsdpst.STORE_CD AND tmp.PROD_CD = tsdpst.PROD_CD GROUP BY tsdpst.SALE_HOUR, tmp.PROD_CLASS_CD, tsdpst.PROD_CD, tmp.PROD_NM ) A GROUP BY A.SALE_HOUR, A.PROD_CLASS_CD, A.PROD_CD, A.PROD_NM, A.TIME_SALE_QTY1, A.TIME_REAL_SALE_AMT1 ) B GROUP BY B.PROD_CLASS_CD, B.PROD_CD, B.PROD_NM ORDER BY B.PROD_CLASS_CD, B.PROD_CD ) T2 WHERE 1=1 AND T2.PROD_CD = T1.PROD_CD

