mapper.select:
/* TimeSaleMomsMapper.getTimeSaleMomsList */
SELECT TO_CHAR(TO_DATE(tsdpt.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE, tsdpt.SALE_HOUR, tsdpt.STORE_CD, tms.STORE_NM, tmp.PROD_NM, SUM(tsdpt.TOT_SALE_QTY) AS TOT_SALE_QTY, SUM(tsdpt.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsdpt.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT
FROM TB_SL_DAILY_PROD_TIME tsdpt, TB_MS_STORE tms, TB_MS_STORE_INFO tmsi, TB_MS_PRODUCT tmp WHERE 1=1 AND tsdpt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdpt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdpt.STORE_CD IN (${storeCdQuery})
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = tmp.STORE_CD START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = tmp.STORE_CD CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = tmp.STORE_CD )
AND tmp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND tmp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND
tmp.PROD_CD = #{item}
AND tmp.HQ_BRAND_CD = #{prodHqBrandCd} -- 상품브랜드 선택일때
-- 상품브랜드 전체일때 AND tmp.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}
AND tms.STORE_CD = tsdpt.STORE_CD AND tmsi.STORE_CD (+)= tms.STORE_CD AND tmp.STORE_CD = tsdpt.STORE_CD AND tmp.PROD_CD = tsdpt.PROD_CD GROUP BY tsdpt.SALE_DATE, tsdpt.SALE_HOUR, tsdpt.STORE_CD, tms.STORE_NM, tsdpt.PROD_CD, tmp.PROD_NM ORDER BY tsdpt.SALE_DATE, tsdpt.SALE_HOUR, tsdpt.STORE_CD, tsdpt.PROD_CD

