mapper.select:
/* MonthMomsMapper.getMonthMomsList */ SELECT TO_CHAR(TO_DATE(tsdt.SALE_YM, 'yyyymm'), 'yyyy-mm') YEAR_MONTH, tsdt.STORE_CNT, tsdt.SALE_CNT, tsdt.TOT_SALE_AMT, tsdt.TOT_DC_AMT, tsdt.REAL_SALE_AMT, tsdt.BILL_CNT, tsdt.BILL_UPRC, tsdt.GA_AMT, tsdt.VAT_AMT, tsdt.TOT_TIP_AMT, tsdt.TOT_ETC_AMT, tsdt.CUP_AMT,
tsdt.PAY_AMT_${item} AS PAY${item} , tsdt.TOT_PAY_AMT, tsdt.GEN_REAL_SALE_AMT, DECODE(tsdt.REAL_SALE_AMT, 0, 0, (tsdt.GEN_REAL_SALE_AMT / tsdt.REAL_SALE_AMT)*100) AS GEN_REAL_SALE_RATE, tsdt.DLVR_REAL_SALE_AMT, DECODE(tsdt.REAL_SALE_AMT, 0, 0, (tsdt.DLVR_REAL_SALE_AMT / tsdt.REAL_SALE_AMT)*100) AS DLVR_REAL_SALE_RATE, tsdt.PACK_REAL_SALE_AMT, DECODE(tsdt.REAL_SALE_AMT, 0, 0, (tsdt.PACK_REAL_SALE_AMT / tsdt.REAL_SALE_AMT)*100) AS PACK_REAL_SALE_RATE , tsdto.SALE_QTY${item} AS SALE_QTY${item} , tsdto.REAL_SALE_AMT${item} AS REAL_SALE_AMT${item}
FROM ( SELECT SUBSTR(SALE_DATE,0,6) AS SALE_YM, COUNT(DISTINCT STORE_CD) AS STORE_CNT, COUNT(DISTINCT SALE_DATE) AS SALE_CNT, SUM(TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(TOT_DC_AMT) AS TOT_DC_AMT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(REAL_SALE_CNT) AS BILL_CNT, ROUND(SUM(REAL_SALE_AMT) / SUM(REAL_SALE_CNT)) AS BILL_UPRC, SUM(REAL_SALE_AMT) - SUM(VAT_AMT) AS GA_AMT, SUM(VAT_AMT) AS VAT_AMT, SUM(TOT_TIP_AMT) AS TOT_TIP_AMT, SUM(TOT_ETC_AMT) AS TOT_ETC_AMT, SUM(CUP_AMT) AS CUP_AMT,
SUM(NVL(PAY_AMT_${item}, 0))
+ SUM(NVL(NON_CASH_AMT, 0)) + SUM(NVL(APPR_CASH_AMT, 0)) AS TOT_PAY_AMT,
SUM(NVL(PAY_AMT_${item}, 0)) AS PAY_AMT_${item}
, SUM(NVL(NON_CASH_AMT, 0)) AS PAY_AMT_02, SUM(NVL(APPR_CASH_AMT, 0)) AS PAY_AMT_021, SUM(CASE WHEN DLVR_ORDER_FG = '1' THEN REAL_SALE_AMT ELSE 0 END) AS GEN_REAL_SALE_AMT, SUM(CASE WHEN DLVR_ORDER_FG = '2' THEN REAL_SALE_AMT ELSE 0 END) AS DLVR_REAL_SALE_AMT, SUM(CASE WHEN DLVR_ORDER_FG = '3' THEN REAL_SALE_AMT ELSE 0 END) AS PACK_REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL_DLVR WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND SALE_DATE BETWEEN #{startMonth}||'01' AND #{endMonth}||'31'
AND STORE_CD IN
#{item}
GROUP BY SUBSTR(SALE_DATE,0,6) ) tsdt , ( SELECT tsdto.SALE_YM , SUM(CASE WHEN DLVR_IN_FG = ${item} THEN SALE_QTY END) AS SALE_QTY${item} , SUM(CASE WHEN DLVR_IN_FG = ${item} THEN REAL_SALE_AMT END) AS REAL_SALE_AMT${item}
FROM TB_SL_MONTHLY_TOTAL_ORDAPP tsdto WHERE tsdto.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdto.STORE_CD IN
#{item}
AND tsdto.SALE_YM BETWEEN #{startMonth} AND #{endMonth} GROUP BY tsdto.SALE_YM ) tsdto WHERE tsdt.SALE_YM = tsdto.SALE_YM (+)
