mapper.select:
/* MonthMomsMapper.getMonthMomsList */
SELECT TO_CHAR(TO_DATE(tsmt.SALE_YM, 'yyyymm'), 'yyyy-mm') AS SALE_YM , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tsmt.STORE_CD , tms.STORE_NM , tsmt.BILL_CNT , DECODE(tsmt.BILL_CNT, 0, 0, ROUND(tsmt.TOT_SALE_AMT/tsmt.BILL_CNT)) AS BILL_UPRC , tsmt.TOT_GUEST_CNT , DECODE(tsmt.TOT_GUEST_CNT, 0, 0, ROUND(tsmt.TOT_SALE_AMT/tsmt.TOT_GUEST_CNT)) AS GUEST_UPRC , tsmt.SALE_CNT , tsmt.TOT_SALE_AMT , tsmt.REAL_SALE_AMT , tsmt.TOT_DC_AMT , tsmt.RTN_SALE_AMT , tsmt.PAY_AMT_02 AS CASH_ATM , tsmt.PAY_AMT_01 AS CARD_AMT , (tsmt.PAY_AMT_03 + tsmt.PAY_AMT_04 + tsmt.PAY_AMT_05 + tsmt.PAY_AMT_06 + tsmt.PAY_AMT_07 + tsmt.PAY_AMT_08 + tsmt.PAY_AMT_09 + tsmt.PAY_AMT_10 + tsmt.PAY_AMT_11 + tsmt.PAY_AMT_12 + tsmt.PAY_AMT_13 + tsmt.PAY_AMT_14 + tsmt.PAY_AMT_15 + tsmt.PAY_AMT_16 + tsmt.PAY_AMT_17 + tsmt.PAY_AMT_18 + tsmt.PAY_AMT_19 + tsmt.PAY_AMT_20) AS ETC_AMT
FROM TB_SL_MONTHLY_TOTAL tsmt , TB_MS_STORE tms WHERE tsmt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsmt.STORE_CD = tms.STORE_CD AND tsmt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmt.SALE_YM BETWEEN #{startMonth} AND #{endMonth}
AND tsmt.STORE_CD IN
#{item}
ORDER BY tsmt.SALE_YM

/* MonthMomsMapper.getMonthMomsExcelList */ SELECT TO_CHAR(TO_DATE(tsmt.SALE_YM, 'yyyymm'), 'yyyy-mm') AS SALE_YM , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tsmt.STORE_CD , tms.STORE_NM , tsmt.BILL_CNT , DECODE(tsmt.BILL_CNT, 0, 0, ROUND(tsmt.TOT_SALE_AMT/tsmt.BILL_CNT)) AS BILL_UPRC , tsmt.TOT_GUEST_CNT , DECODE(tsmt.TOT_GUEST_CNT, 0, 0, ROUND(tsmt.TOT_SALE_AMT/tsmt.TOT_GUEST_CNT)) AS GUEST_UPRC , tsmt.SALE_CNT , tsmt.TOT_SALE_AMT , tsmt.REAL_SALE_AMT , tsmt.TOT_DC_AMT , tsmt.RTN_SALE_AMT , tsmt.PAY_AMT_02 AS CASH_ATM , tsmt.PAY_AMT_01 AS CARD_AMT , (tsmt.PAY_AMT_03 + tsmt.PAY_AMT_04 + tsmt.PAY_AMT_05 + tsmt.PAY_AMT_06 + tsmt.PAY_AMT_07 + tsmt.PAY_AMT_08 + tsmt.PAY_AMT_09 + tsmt.PAY_AMT_10 + tsmt.PAY_AMT_11 + tsmt.PAY_AMT_12 + tsmt.PAY_AMT_13 + tsmt.PAY_AMT_14 + tsmt.PAY_AMT_15 + tsmt.PAY_AMT_16 + tsmt.PAY_AMT_17 + tsmt.PAY_AMT_18 + tsmt.PAY_AMT_19 + tsmt.PAY_AMT_20) AS ETC_AMT FROM TB_SL_MONTHLY_TOTAL tsmt , TB_MS_STORE tms WHERE tsmt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsmt.STORE_CD = tms.STORE_CD AND tsmt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmt.SALE_YM BETWEEN #{startMonth} AND #{endMonth}
AND tsmt.STORE_CD IN
#{item}
ORDER BY tsmt.SALE_YM
