mapper.select:
/* DaySaleMrpizzaMapper.getDaySaleMrpizzaList */ SELECT TO_CHAR(TO_DATE(tsshp.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , tsshp.YOIL , tsshp.STORE_CD , tms.STORE_NM , tsshp.BILL_CNT , tsshp.TOT_SALE_AMT , tsshp.REAL_SALE_AMT , NVL(tsshp.PAY${item}, 0) + NVL(tsshp.PAY02, 0) + NVL(tsshp.PAY021, 0) - (NVL(tsshp.PAY01, 0) + NVL(tsshp.PAY02, 0) + NVL(tsshp.PAY18, 0) + NVL(tsshp.PAY021, 0)) -- 현금, 카드, 가승인, 현금영수증 제외 AS TOT_ETC_PAY_AMT , tsshp.PAY${item} AS PAY${item}
, NVL(tsshp.PAY02, 0) + NVL(tsshp.PAY021, 0) AS PAY02 -- 현금 + 현금영수증 , NVL(tsshp.DC${item}, 0) - NVL(tsshp.DC05, 0) -- 서비스 할인 제외 AS TOT_DC_AMT , tsshp.DC${item} AS DC${item}
FROM ( SELECT SALE_DATE , TO_CHAR(TO_DATE(SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL , STORE_CD , SUM(BILL_CNT) AS BILL_CNT , SUM(TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(TOT_DC_AMT) AS TOT_DC_AMT , SUM(PAY_AMT_${item}) AS PAY${item}
, NVL(SUM(NON_CASH_AMT), 0) AS PAY02 , NVL(SUM(APPR_CASH_AMT), 0) AS PAY021 , SUM(DC_AMT_${item}) AS DC${item}
FROM TB_SL_DAILY_TOTAL tsdt WHERE tsdt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdt.STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tsdt.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY SALE_DATE , STORE_CD ) tsshp , TB_MS_STORE tms WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tms.STORE_CD = tsshp.STORE_CD ORDER BY tsshp.SALE_DATE DESC , tsshp.STORE_CD ASC
