/* MenuSaleMrpizzaMapper.getMenuSaleMrpizzaList */
SELECT TO_CHAR(TO_DATE(tssd.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , TO_CHAR(TO_DATE(tssd.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL , tssd.STORE_CD , tms.STORE_NM , tssd.POS_NO , tssd.BILL_NO , TO_CHAR(TO_DATE(tooh.ORDER_END_DT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS ORDER_END_DT , TO_CHAR(TO_DATE(tooh.BILL_DT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS BILL_DT , tmpcv.L_CLASS_CD , tmpcv.L_CLASS_NM , tmpcv.M_CLASS_CD , tmpcv.M_CLASS_NM , tmpcv.S_CLASS_CD , tmpcv.S_CLASS_NM , tssd.PROD_CD , tmp.PROD_NM , tmp.REMARK , NVL(tssd.SALE_QTY, 0) AS SALE_QTY , NVL(tssd.SALE_AMT, 0) AS SALE_AMT , NVL(tssd.DC_AMT, 0) AS DC_AMT , NVL(tssd.REAL_SALE_AMT, 0) AS REAL_SALE_AMT
FROM TB_SL_SALE_DTL tssd , TB_MS_STORE tms , TB_MS_PRODUCT tmp , TB_MS_PRODUCT_CLASS_V03 tmpcv , ( SELECT A.HQ_OFFICE_CD , A.HQ_BRAND_CD , A.STORE_CD , A.SALE_DATE , A.POS_NO , A.BILL_NO , A.ORDER_NO , B.ORDER_END_DT -- 주문일시 , A.BILL_DT -- 결제시간 FROM TB_SL_SALE_HDR A , TB_OD_ORDER_HDR B WHERE A.HQ_OFFICE_CD = #{hqOfficeCd} AND A.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND A.STORE_CD IN (${storeCdQuery})
AND B.STORE_CD (+) = A.STORE_CD AND B.SALE_DATE (+) = A.SALE_DATE AND B.ORDER_NO (+) = A.ORDER_NO ) tooh WHERE tssd.HQ_OFFICE_CD = #{hqOfficeCd} AND tssd.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tssd.STORE_CD IN (${storeCdQuery})
AND tmp.PROD_CLASS_CD IN
#{item}
AND tssd.PROD_CD LIKE '%'||#{prodCd}||'%'
AND tmp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND
tssd.PROD_CD = #{item}
AND tms.HQ_OFFICE_CD = tssd.HQ_OFFICE_CD AND tms.STORE_CD = tssd.STORE_CD AND tmp.STORE_CD = tssd.STORE_CD AND tmp.PROD_CD = tssd.PROD_CD AND tmpcv.STORE_CD = tmp.STORE_CD AND tmpcv.PROD_CLASS_CD = tmp.PROD_CLASS_CD AND tooh.HQ_OFFICE_CD (+)= tssd.HQ_OFFICE_CD AND tooh.HQ_BRAND_CD (+)= tssd.HQ_BRAND_CD AND tooh.STORE_CD (+)= tssd.STORE_CD AND tooh.SALE_DATE (+)= tssd.SALE_DATE AND tooh.POS_NO (+)= tssd.POS_NO AND tooh.BILL_NO (+)= tssd.BILL_NO ORDER BY tssd.SALE_DATE DESC, tssd.STORE_CD, tssd.POS_NO, tssd.BILL_NO, tssd.BILL_DTL_NO ASC

