mapper.select:
/* PayDayMapper.getPayDayList */ SELECT TO_CHAR(TO_DATE(tsshp.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , TO_CHAR(TO_DATE(tsshp.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL , tsshp.STORE_CNT , tsshp.TOT_SALE_AMT , tsshp.TOT_DC_AMT , tsshp.REAL_SALE_AMT , tsshp.GA_AMT , tsshp.VAT_AMT , tsshp.TOT_TIP_AMT , tsshp.TOT_ETC_AMT , tsshp.CUP_AMT , tsshp.BILL_CNT , tsshp.BILL_UPRC , tsshp.TOT_PAY_AMT , tsshp.PAY_CNT${item} AS PAY_CNT${item} , tsshp.PAY_AMT${item} AS PAY_AMT${item} , DECODE(tsshp.TOT_PAY_AMT, 0, 0, ROUND(tsshp.PAY_AMT${item}/tsshp.TOT_PAY_AMT, 4) * 100) AS PAY_RATE${item}
FROM ( SELECT SALE_DATE , COUNT(DISTINCT tsdt.STORE_CD) AS STORE_CNT , SUM(TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(TOT_DC_AMT) AS TOT_DC_AMT , SUM(REAL_SALE_AMT) AS REAL_SALE_AMT , SUM(REAL_SALE_AMT) - SUM(VAT_AMT) AS GA_AMT , SUM(VAT_AMT) AS VAT_AMT , SUM(TOT_TIP_AMT) AS TOT_TIP_AMT , SUM(TOT_ETC_AMT) AS TOT_ETC_AMT , SUM(CUP_AMT) AS CUP_AMT , SUM(BILL_CNT) AS BILL_CNT , DECODE(SUM(BILL_CNT), 0, 0, ROUND(SUM(REAL_SALE_AMT) / SUM(BILL_CNT))) AS BILL_UPRC , SUM(PAY_AMT_${item}) AS TOT_PAY_AMT , SUM(PAY_CNT_${item}) AS PAY_CNT${item} , SUM(PAY_AMT_${item}) AS PAY_AMT${item}
FROM TB_SL_DAILY_TOTAL tsdt , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tsdt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdt.STORE_CD = tms.STORE_CD AND tsdt.STORE_CD = tmsi.STORE_CD(+) AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdt.STORE_CD IN
#{item}
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
GROUP BY SALE_DATE ) tsshp ORDER BY tsshp.SALE_DATE DESC
