mapper.select:
/* PayFgMapper.getPayFgList */
SELECT amt.PAY_CD , cnt.PAY_CNT , amt.PAY_AMT , ROUND(RATIO_TO_REPORT(amt.PAY_AMT) OVER(), 4) * 100 AS RATE
, amt.BRANCH_CD , amt.BRANCH_NM
, amt.BRANCH_CD , amt.BRANCH_NM , amt.STORE_CD , amt.STORE_NM
FROM ( SELECT * FROM ( SELECT SUM(tsdt.PAY_AMT_${item}) AS PAY_AMT_${item}
, tms.BRANCH_CD , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM
, tms.BRANCH_CD , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tsdt.STORE_CD , tms.STORE_NM
FROM TB_SL_DAILY_TOTAL tsdt , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tsdt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdt.STORE_CD = tms.STORE_CD AND tsdt.STORE_CD = tmsi.STORE_CD(+) AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdt.STORE_CD IN (${storeCdQuery})
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
GROUP BY tms.BRANCH_CD
GROUP BY tms.BRANCH_CD, tsdt.STORE_CD, tms.STORE_NM
) UNPIVOT ( PAY_AMT FOR PAY_CD IN (
PAY_AMT_${item} AS '${item}'
) ) ) AMT , ( SELECT * FROM ( SELECT SUM(tsdt.PAY_CNT_${item}) AS PAY_CNT_${item}
, tms.BRANCH_CD , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM
, tms.BRANCH_CD , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tsdt.STORE_CD , tms.STORE_NM
FROM TB_SL_DAILY_TOTAL tsdt , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tsdt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdt.STORE_CD = tms.STORE_CD AND tsdt.STORE_CD = tmsi.STORE_CD(+) AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdt.STORE_CD IN (${storeCdQuery})
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
GROUP BY tms.BRANCH_CD
GROUP BY tms.BRANCH_CD, tsdt.STORE_CD, tms.STORE_NM
) UNPIVOT ( PAY_CNT FOR PAY_CD IN (
PAY_CNT_${item} AS '${item}'
) ) ) CNT WHERE AMT.PAY_CD = CNT.PAY_CD -- AND (AMT.PAY_AMT > 0 OR CNT.PAY_CNT > 0)
-- AND AMT.BRANCH_CD = CNT.BRANCH_CD -- AND AMT.BRANCH_NM = CNT.BRANCH_NM
-- AND AMT.BRANCH_CD = CNT.BRANCH_CD -- AND AMT.BRANCH_NM = CNT.BRANCH_NM AND AMT.STORE_CD = CNT.STORE_CD AND AMT.STORE_NM = CNT.STORE_NM

