        WHERE 	AMT.PAY_CD = CNT.PAY_CD
--         AND     (AMT.PAY_AMT > 0 OR CNT.PAY_CNT > 0)

            --         AND 	AMT.BRANCH_CD = CNT.BRANCH_CD
            --         AND 	AMT.BRANCH_NM = CNT.BRANCH_NM


            --         AND 	AMT.BRANCH_CD = CNT.BRANCH_CD
            --         AND 	AMT.BRANCH_NM = CNT.BRANCH_NM
            AND 	AMT.STORE_CD = CNT.STORE_CD
            AND 	AMT.STORE_NM = CNT.STORE_NM
