mapper.select:
/* PayFgMapper.getPayFgList */
SELECT tsdp.STORE_CD , tms.STORE_NM , tsdp.PAY_CD , SUM(tsdp.TOT_SALE_QTY) AS TOT_SALE_QTY , SUM(tsdp.TOT_SALE_AMT) AS TOT_SALE_AMT , ROUND(RATIO_TO_REPORT(SUM(tsdp.TOT_SALE_AMT)) OVER(), 4) * 100 AS RATE
FROM TB_SL_DAILY_PAY tsdp , TB_MS_STORE tms WHERE tsdp.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdp.STORE_CD = tms.STORE_CD AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdp.STORE_CD IN
#{item}
GROUP BY tsdp.STORE_CD, tms.STORE_NM, tsdp.PAY_CD ORDER BY RATE DESC

/* PayFgMapper.getPayFgExcelList */ SELECT tsdp.STORE_CD , tms.STORE_NM , tsdp.PAY_CD , SUM(tsdp.TOT_SALE_QTY) AS TOT_SALE_QTY , SUM(tsdp.TOT_SALE_AMT) AS TOT_SALE_AMT , ROUND(RATIO_TO_REPORT(SUM(tsdp.TOT_SALE_AMT)) OVER(), 4) * 100 AS RATE FROM TB_SL_DAILY_PAY tsdp , TB_MS_STORE tms WHERE tsdp.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdp.STORE_CD = tms.STORE_CD AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdp.STORE_CD IN
#{item}
GROUP BY tsdp.STORE_CD, tms.STORE_NM, tsdp.PAY_CD ORDER BY RATE DESC
