mapper.select:
/* PayMonthMapper.getPayMonthList */ SELECT TO_CHAR(TO_DATE(tsdt.SALE_YM, 'yyyymm'), 'yyyy-mm') YEAR_MONTH, tsdt.STORE_CNT, tsdt.SALE_CNT, tsdt.TOT_SALE_AMT, tsdt.TOT_DC_AMT, tsdt.REAL_SALE_AMT, tsdt.BILL_CNT, tsdt.BILL_UPRC, tsdt.GA_AMT, tsdt.VAT_AMT, tsdt.TOT_TIP_AMT, tsdt.TOT_ETC_AMT, tsdt.TOT_PAY_AMT,
tsdt.PAY_CNT${item} AS PAY_CNT${item}, tsdt.PAY_AMT${item} AS PAY_AMT${item}, DECODE(tsdt.TOT_PAY_AMT, 0, 0, ROUND(tsdt.PAY_AMT${item}/tsdt.TOT_PAY_AMT, 4) * 100) AS PAY_RATE${item}
FROM ( SELECT SUBSTR(SALE_DATE,0,6) AS SALE_YM, COUNT(DISTINCT tsdt.STORE_CD) AS STORE_CNT, COUNT(DISTINCT SALE_DATE) AS SALE_CNT, SUM(TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(TOT_DC_AMT) AS TOT_DC_AMT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(BILL_CNT) AS BILL_CNT, ROUND(SUM(REAL_SALE_AMT) / SUM(BILL_CNT)) AS BILL_UPRC, SUM(REAL_SALE_AMT) - SUM(VAT_AMT) AS GA_AMT, SUM(VAT_AMT) AS VAT_AMT, SUM(TOT_TIP_AMT) AS TOT_TIP_AMT, SUM(TOT_ETC_AMT) AS TOT_ETC_AMT,
SUM(NVL(PAY_AMT_${item}, 0)) AS TOT_PAY_AMT,
SUM(NVL(PAY_CNT_${item}, 0)) AS PAY_CNT${item}, SUM(NVL(PAY_AMT_${item}, 0)) AS PAY_AMT${item}
FROM TB_SL_DAILY_TOTAL tsdt , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tsdt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdt.STORE_CD = tms.STORE_CD AND tsdt.STORE_CD = tmsi.STORE_CD(+) AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.SALE_DATE BETWEEN #{startMonth}||'01' AND #{endMonth}||'31'
AND tsdt.STORE_CD IN
#{item}
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
GROUP BY SUBSTR(tsdt.SALE_DATE,0,6) ) tsdt ORDER BY tsdt.SALE_YM DESC
