GROUP BY TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'YYYYMMDD'), 'DY') , TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'YYYYMMDD'), 'd') ) A , ( SELECT TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL , TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'YYYYMMDD'), 'd') AS YOIL_ORDER , SUM(NVL(tsdt.REAL_SALE_CNT, 0)) AS REAL_SALE_CNT , ROUND(SUM(NVL(tsdt.REAL_SALE_AMT,0)) / SUM(NVL(tsdt.REAL_SALE_CNT,0))) AS BILL_UPRC , SUM(NVL(tsdt.SALE_QTY, 0)) AS SALE_QTY , SUM(NVL(tsdt.TOT_SALE_AMT, 0)) AS TOT_SALE_AMT , SUM(NVL(tsdt.TOT_DC_AMT, 0)) AS TOT_DC_AMT , SUM(NVL(tsdt.REAL_SALE_AMT, 0)) AS REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL tsdt , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE 1=1 AND tsdt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdt.STORE_CD = tms.STORE_CD AND tms.STORE_CD = tmsi.STORE_CD AND tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdt.SALE_DATE BETWEEN #{compStartDate} AND #{compEndDate}
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
