mapper.select:
/* PeriodMomsMapper.getPeriodMomsList */
SELECT A.YOIL , A.TOT_SALE_QTY , A.TOT_SALE_AMT , A.REAL_SALE_AMT , ROUND(RATIO_TO_REPORT(A.TOT_SALE_QTY) OVER(), 4) * 100 AS RATE , B.TOT_SALE_QTY AS TOT_SALE_QTY1 , B.TOT_SALE_AMT AS TOT_SALE_AMT1 , B.REAL_SALE_AMT AS REAL_SALE_AMT1 , ROUND(RATIO_TO_REPORT(B.TOT_SALE_QTY) OVER(), 4) * 100 AS RATE1 , DECODE(B.TOT_SALE_QTY, 0, 0, ROUND(A.TOT_SALE_QTY/B.TOT_SALE_QTY * 100), 2) AS RATE_SALE_QTY , DECODE(B.TOT_SALE_AMT, 0, 0, ROUND(A.TOT_SALE_AMT/B.TOT_SALE_AMT * 100), 2) AS RATE_SALE_AMT , DECODE(B.REAL_SALE_AMT, 0, 0, ROUND(A.REAL_SALE_AMT/B.REAL_SALE_AMT * 100), 2) AS RATE_REAL_SALE_AMT
FROM ( SELECT TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL , tsdt.SALE_CNT AS TOT_SALE_QTY , tsdt.TOT_SALE_AMT , tsdt.REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL tsdt WHERE tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND STORE_CD IN
#{item}
) A , ( SELECT TO_CHAR(TO_DATE(tsdt.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL , tsdt.SALE_CNT AS TOT_SALE_QTY , tsdt.TOT_SALE_AMT , tsdt.REAL_SALE_AMT FROM TB_SL_DAILY_TOTAL tsdt WHERE tsdt.HQ_OFFICE_CD = #{hqOfficeCd} AND SALE_DATE BETWEEN #{compStartDate} AND #{compEndDate}
AND STORE_CD IN
#{item}
) B WHERE A.YOIL = B.YOIL(+) ORDER BY A.YOIL

