mapper.select:
/* DayProdMapper.getDayProdList */

SELECT
TO_CHAR(TO_DATE(tsdp.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE,
TO_CHAR(TO_DATE(tsdp.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL,
(SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM,
tsdp.STORE_CD,
tms.STORE_NM,
FN_GET_PROD_CLASS_CD_NM_STR(tmp.STORE_CD, tmp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM,
tsdp.PROD_CD,
tmp.PROD_NM,
tsdp.TOT_SALE_QTY AS SALE_QTY,
tsdp.TOT_DC_AMT AS DC_AMT,
tsdp.TOT_SALE_AMT,
tsdp.REAL_SALE_AMT

FROM TB_SL_DAILY_PROD tsdp,
TB_MS_STORE tms,
TB_MS_STORE_INFO tmsi,
TB_MS_PRODUCT tmp
WHERE 1=1
AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate}

    AND tsdp.STORE_CD IN

        #{item}



    AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때



        -- 매장브랜드 전체일때
        AND tms.HQ_BRAND_CD IN

            #{item}




    AND tmp.PROD_CD LIKE '%'||#{prodCd}||'%'


    AND tmp.PROD_NM LIKE '%'||#{prodNm}||'%'


    AND tmp.PROD_CLASS_CD IN (
                                SELECT
                                PROD_CLASS_CD
                                FROM TB_HQ_PRODUCT_CLASS
                                WHERE HQ_OFFICE_CD = #{hqOfficeCd}
                                START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd}
                                CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd}
                            )


    AND tmp.PROD_CD IN

        #{item}



    AND tmp.HQ_BRAND_CD = #{prodHqBrandCd} -- 상품브랜드 선택일때



        -- 상품브랜드 전체일때
        AND tmp.HQ_BRAND_CD IN

            #{item}




    AND tmsi.MOMS_TEAM = #{momsTeam}


    AND tmsi.MOMS_AC_SHOP = #{momsAcShop}


    AND tmsi.MOMS_AREA_FG = #{momsAreaFg}


    AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}


    AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}


    AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}


    AND tms.BRANCH_CD = #{branchCd}

AND tms.STORE_CD = tsdp.STORE_CD
AND tmsi.STORE_CD (+)= tms.STORE_CD
AND tmp.STORE_CD = tsdp.STORE_CD
AND tmp.PROD_CD = tsdp.PROD_CD
ORDER BY tsdp.SALE_DATE, tsdp.STORE_CD, tsdp.PROD_CD

/* DayProdMapper.getDayProdExcelList */
SELECT
TO_CHAR(TO_DATE(tsdp.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE,
TO_CHAR(TO_DATE(tsdp.SALE_DATE, 'YYYYMMDD'), 'DY') AS YOIL,
(SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM,
tsdp.STORE_CD,
tms.STORE_NM,
FN_GET_PROD_CLASS_CD_NM_STR(tmp.STORE_CD, tmp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM,
tsdp.PROD_CD,
tmp.PROD_NM,
tsdp.TOT_SALE_QTY AS SALE_QTY,
tsdp.TOT_DC_AMT AS DC_AMT,
tsdp.TOT_SALE_AMT,
tsdp.REAL_SALE_AMT
FROM TB_SL_DAILY_PROD tsdp,
TB_MS_STORE tms,
TB_MS_STORE_INFO tmsi,
TB_MS_PRODUCT tmp
WHERE 1=1
AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate}

    AND tsdp.STORE_CD IN

        #{item}



    AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때



        -- 매장브랜드 전체일때
        AND tms.HQ_BRAND_CD IN

            #{item}




    AND tmp.PROD_CD LIKE '%'||#{prodCd}||'%'


    AND tmp.PROD_NM LIKE '%'||#{prodNm}||'%'


    AND tmp.PROD_CLASS_CD IN (
    SELECT
    PROD_CLASS_CD
    FROM TB_HQ_PRODUCT_CLASS
    WHERE HQ_OFFICE_CD = #{hqOfficeCd}
    START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd}
    CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd}
    )


    AND tmp.PROD_CD IN

        #{item}



    AND tmp.HQ_BRAND_CD = #{prodHqBrandCd} -- 상품브랜드 선택일때



        -- 상품브랜드 전체일때
        AND tmp.HQ_BRAND_CD IN

            #{item}




    AND tmsi.MOMS_TEAM = #{momsTeam}


    AND tmsi.MOMS_AC_SHOP = #{momsAcShop}


    AND tmsi.MOMS_AREA_FG = #{momsAreaFg}


    AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}


    AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}


    AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}


    AND tms.BRANCH_CD = #{branchCd}

AND tms.STORE_CD = tsdp.STORE_CD
AND tmsi.STORE_CD (+)= tms.STORE_CD
AND tmp.STORE_CD = tsdp.STORE_CD
AND tmp.PROD_CD = tsdp.PROD_CD
ORDER BY tsdp.SALE_DATE, tsdp.STORE_CD, tsdp.PROD_CD

/* DayProdMapper.getUserBrandComboList */
SELECT '' AS VALUE, '전체' AS NAME FROM DUAL UNION SELECT HQ_BRAND_CD AS VALUE, HQ_BRAND_NM AS NAME FROM TB_HQ_BRAND WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND USE_YN = 'Y'
AND HQ_BRAND_CD IN
#{item}
ORDER BY VALUE NULLS FIRST
SELECT '' AS VALUE, '전체' AS NAME FROM DUAL UNION SELECT MS_BRAND_CD AS VALUE, MS_BRAND_NM AS NAME FROM TB_MS_BRAND WHERE STORE_CD = #{storeCd} AND USE_YN = 'Y' ORDER BY VALUE NULLS FIRST

/* DayProdMapper.getUserHqNmcodeCdList */
SELECT the.MOMS_TEAM FROM TB_HQ_EMPLOYEE the WHERE 1=1 AND the.USER_ID = #{userId}
SELECT the.MOMS_AC_SHOP FROM TB_HQ_EMPLOYEE the WHERE 1=1 AND the.USER_ID = #{userId}
SELECT the.MOMS_AREA_FG FROM TB_HQ_EMPLOYEE the WHERE 1=1 AND the.USER_ID = #{userId}
SELECT the.MOMS_COMMERCIAL FROM TB_HQ_EMPLOYEE the WHERE 1=1 AND the.USER_ID = #{userId}
SELECT the.MOMS_SHOP_TYPE FROM TB_HQ_EMPLOYEE the WHERE 1=1 AND the.USER_ID = #{userId}
SELECT the.MOMS_STORE_MANAGE_TYPE FROM TB_HQ_EMPLOYEE the WHERE 1=1 AND the.USER_ID = #{userId}

/* DayProdMapper.getUserHqNmcodeComboList */
SELECT thn.NMCODE_CD AS VALUE, thn.NMCODE_NM AS NAME FROM TB_HQ_NMCODE thn WHERE 1=1 AND thn.HQ_OFFICE_CD = #{hqOfficeCd} AND thn.NMCODE_GRP_CD = #{nmcodeGrpCd}
AND thn.NMCODE_CD = #{userHqNmcodeCd} ORDER BY thn.NMCODE_CD
UNION SELECT '' AS VALUE, '전체' AS NAME FROM DUAL ORDER BY VALUE NULLS FIRST

/* DayProdMapper.getUserBranchCdList */
SELECT
the.BRANCH_CD
FROM TB_HQ_EMPLOYEE the
WHERE 1=1
AND the.USER_ID = #{userId}

/* DayProdMapper.getUserBranchComboList */
SELECT thbm.BRANCH_CD AS VALUE, thbm.BRANCH_NM AS NAME FROM TB_HQ_BRANCH_MOMS thbm WHERE 1=1 AND thbm.HQ_OFFICE_CD = #{hqOfficeCd}
AND thbm.BRANCH_CD = #{userHqNmcodeCd} ORDER BY thbm.BRANCH_CD
UNION SELECT '' AS VALUE, '전체' AS NAME FROM DUAL ORDER BY VALUE NULLS FIRST
