mapper.select:
/* MonthProdMapper.getMonthProdList */
SELECT TO_CHAR(TO_DATE(tsmp.SALE_YM, 'YYYYMM'), 'YYYY-MM') AS SALE_YM , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tsmp.STORE_CD , tms.STORE_NM , FN_GET_PROD_CLASS_CD_NM_STR(thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM , (SELECT tmp.PROD_NM FROM TB_MS_PRODUCT tmp WHERE tmp.STORE_CD = tsmp.PROD_CD AND tmp.PROD_CD = tsmp.PROD_CD) AS PROD_NM , tsmp.TOT_SALE_QTY AS SALE_QTY , tsmp.TOT_DC_AMT AS DC_AMT , tsmp.TOT_SALE_AMT , tsmp.REAL_SALE_AMT
FROM TB_SL_MONTHLY_PROD tsmp , TB_MS_STORE tms , TB_HQ_PRODUCT thp WHERE tsmp.STORE_CD = tms.STORE_CD AND tsmp.HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND tsmp.PROD_CD = thp.PROD_CD AND tsmp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmp.SALE_YM BETWEEN #{startMonth} AND #{endMonth}
AND tsmp.STORE_CD IN
#{item}
AND thp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
ORDER BY tsmp.SALE_YM, tsmp.STORE_CD, tsmp.PROD_CD

/* MonthProdMapper.getMonthProdExcelList */ SELECT TO_CHAR(TO_DATE(tsmp.SALE_YM, 'YYYYMM'), 'YYYY-MM') AS SALE_YM , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tsmp.STORE_CD , tms.STORE_NM , FN_GET_PROD_CLASS_CD_NM_STR(thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM , (SELECT tmp.PROD_NM FROM TB_MS_PRODUCT tmp WHERE tmp.STORE_CD = tsmp.PROD_CD AND tmp.PROD_CD = tsmp.PROD_CD) AS PROD_NM , tsmp.TOT_SALE_QTY AS SALE_QTY , tsmp.TOT_DC_AMT AS DC_AMT , tsmp.TOT_SALE_AMT , tsmp.REAL_SALE_AMT FROM TB_SL_MONTHLY_PROD tsmp , TB_MS_STORE tms , TB_HQ_PRODUCT thp WHERE tsmp.STORE_CD = tms.STORE_CD AND tsmp.HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND tsmp.PROD_CD = thp.PROD_CD AND tsmp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmp.SALE_YM BETWEEN #{startMonth} AND #{endMonth}
AND tsmp.STORE_CD IN
#{item}
AND thp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
ORDER BY tsmp.SALE_YM, tsmp.STORE_CD, tsmp.PROD_CD
