mapper.select:
/* MonthProdMapper.getMonthProdList */
SELECT
TO_CHAR(TO_DATE(tsmps.SALE_YM, 'YYYYMM'), 'YYYY-MM') AS SALE_YM,
TO_CHAR(TO_DATE(#{startMonth}, 'YYYYMM'), 'YYYY-MM') AS DAY_FROM, TO_CHAR(TO_DATE(#{endMonth}, 'YYYYMM'), 'YYYY-MM') AS DAY_TO,
L_CLASS_CD, L_CLASS_NM, M_CLASS_CD, M_CLASS_NM, S_CLASS_CD, S_CLASS_NM, tsmps.SIDE_PROD_CD AS PROD_CD, tmp.PROD_NM, (CASE tsmps.SEL_TYPE_FG WHEN 'P' THEN '사이드모상품' WHEN 'S' THEN '사이드상품' WHEN 'N' THEN '일반판매상품' END) AS SEL_TYPE_FG, (CASE tsmps.SEL_TYPE_FG WHEN 'S' THEN tsmps.PROD_CD END) AS SIDE_PROD_CD, (CASE tsmps.SEL_TYPE_FG WHEN 'S' THEN 'ㅤ- ▶ ' || tmps2.PROD_NM ELSE tmps2.PROD_NM END) AS SIDE_PROD_NM, SUM(CASE WHEN tsmps.SEL_TYPE_FG = 'N' OR tsmps.SEL_TYPE_FG = 'P' THEN tsmps.TOT_SALE_QTY END) AS SALE_QTY1, SUM(CASE WHEN tsmps.SEL_TYPE_FG = 'N' OR tsmps.SEL_TYPE_FG = 'S' THEN tsmps.TOT_SALE_QTY END) AS SALE_QTY2, SUM(tsmps.TOT_SALE_QTY) AS SALE_QTY3, SUM(tsmps.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsmps.TOT_DC_AMT) AS DC_AMT, SUM(CASE WHEN tsmps.SEL_TYPE_FG = 'N' OR tsmps.SEL_TYPE_FG = 'P' THEN tsmps.REAL_SALE_AMT END) AS REAL_SALE_AMT1, SUM(CASE WHEN tsmps.SEL_TYPE_FG = 'N' OR tsmps.SEL_TYPE_FG = 'S' THEN tsmps.REAL_SALE_AMT END) AS REAL_SALE_AMT2, SUM(tsmps.REAL_SALE_AMT) AS REAL_SALE_AMT3
FROM
TB_SL_MONTHLY_PROD_SDSEL tsmps,
TB_SL_MONTHLY_PROD_SDSEL_MOMS_V01 tsmps,
TB_MS_STORE tms, TB_MS_STORE_INFO tmsi, TB_MS_PRODUCT tmp, TB_MS_PRODUCT tmps2, -- 사이드 TB_MS_PRODUCT_CLASS_V02 tmpcv WHERE 1=1 AND tsmps.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmps.SALE_YM BETWEEN #{startMonth} AND #{endMonth}
AND
tsmps.STORE_CD = #{item}
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmps2.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = tmps2.STORE_CD START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = tmps2.STORE_CD CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = tmps2.STORE_CD )
AND tmps2.PROD_CD LIKE '%'||#{prodCd}||'%'
AND tmps2.PROD_NM LIKE '%'||#{prodNm}||'%'
AND
tmps2.PROD_CD = #{item}
AND tmps2.HQ_BRAND_CD = #{prodHqBrandCd} -- 상품브랜드 선택일때
-- 상품브랜드 전체일때 AND tmps2.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tsmps.SEL_TYPE_FG IN ('N', 'P')
AND tsmps.SEL_TYPE_FG IN ('N', 'S')
AND tms.STORE_CD = tsmps.STORE_CD AND tmsi.STORE_CD (+)= tms.STORE_CD AND tmp.STORE_CD = tsmps.STORE_CD AND tmp.PROD_CD = tsmps.PROD_CD AND tmps2.STORE_CD = tsmps.STORE_CD AND tmps2.PROD_CD = tsmps.PROD_CD AND tmpcv.STORE_CD = tmps2.STORE_CD AND tmpcv.PROD_CD = tmps2.PROD_CD
GROUP BY tsmps.SALE_YM, L_CLASS_CD, L_CLASS_NM, M_CLASS_CD, M_CLASS_NM, S_CLASS_CD, S_CLASS_NM, tsmps.PROD_CD, tmp.PROD_NM, tsmps.SEL_TYPE_FG, tsmps.SIDE_PROD_CD, tmps2.PROD_NM ORDER BY tsmps.SALE_YM, tsmps.SIDE_PROD_CD, (CASE tsmps.SEL_TYPE_FG WHEN 'S' THEN tsmps.PROD_CD END) DESC
GROUP BY L_CLASS_CD, L_CLASS_NM, M_CLASS_CD, M_CLASS_NM, S_CLASS_CD, S_CLASS_NM, tsmps.PROD_CD, tmp.PROD_NM, tsmps.SEL_TYPE_FG, tsmps.SIDE_PROD_CD, tmps2.PROD_NM ORDER BY tsmps.SIDE_PROD_CD, (CASE tsmps.SEL_TYPE_FG WHEN 'S' THEN tsmps.PROD_CD END) DESC

/* MonthProdMapper.getMonthProdExcelList */ SELECT
TO_CHAR(TO_DATE(tsmps.SALE_YM, 'YYYYMM'), 'YYYY-MM') AS SALE_YM,
TO_CHAR(TO_DATE(#{startMonth}, 'YYYYMM'), 'YYYY-MM') AS DAY_FROM, TO_CHAR(TO_DATE(#{endMonth}, 'YYYYMM'), 'YYYY-MM') AS DAY_TO,
L_CLASS_CD, L_CLASS_NM, M_CLASS_CD, M_CLASS_NM, S_CLASS_CD, S_CLASS_NM, tsmps.SIDE_PROD_CD AS PROD_CD, tmp.PROD_NM, (CASE tsmps.SEL_TYPE_FG WHEN 'P' THEN '사이드모상품' WHEN 'S' THEN '사이드상품' WHEN 'N' THEN '일반판매상품' END) AS SEL_TYPE_FG, (CASE tsmps.SEL_TYPE_FG WHEN 'S' THEN tsmps.PROD_CD END) AS SIDE_PROD_CD, (CASE tsmps.SEL_TYPE_FG WHEN 'S' THEN 'ㅤ- ▶ ' || tmps2.PROD_NM ELSE tmps2.PROD_NM END) AS SIDE_PROD_NM, SUM(CASE WHEN tsmps.SEL_TYPE_FG = 'N' OR tsmps.SEL_TYPE_FG = 'P' THEN tsmps.TOT_SALE_QTY END) AS SALE_QTY1, SUM(CASE WHEN tsmps.SEL_TYPE_FG = 'N' OR tsmps.SEL_TYPE_FG = 'S' THEN tsmps.TOT_SALE_QTY END) AS SALE_QTY2, SUM(tsmps.TOT_SALE_QTY) AS SALE_QTY3, SUM(tsmps.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsmps.TOT_DC_AMT) AS DC_AMT, SUM(CASE WHEN tsmps.SEL_TYPE_FG = 'N' OR tsmps.SEL_TYPE_FG = 'P' THEN tsmps.REAL_SALE_AMT END) AS REAL_SALE_AMT1, SUM(CASE WHEN tsmps.SEL_TYPE_FG = 'N' OR tsmps.SEL_TYPE_FG = 'S' THEN tsmps.REAL_SALE_AMT END) AS REAL_SALE_AMT2, SUM(tsmps.REAL_SALE_AMT) AS REAL_SALE_AMT3
FROM
TB_SL_MONTHLY_PROD_SDSEL tsmps,
TB_SL_MONTHLY_PROD_SDSEL_MOMS_V01 tsmps,
TB_MS_STORE tms, TB_MS_STORE_INFO tmsi, TB_MS_PRODUCT tmp, TB_MS_PRODUCT tmps2, -- 사이드 TB_MS_PRODUCT_CLASS_V02 tmpcv WHERE 1=1 AND tsmps.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmps.SALE_YM BETWEEN #{startMonth} AND #{endMonth}
AND
tsmps.STORE_CD = #{item}
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmps2.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = tmps2.STORE_CD START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = tmps2.STORE_CD CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = tmps2.STORE_CD )
AND tmps2.PROD_CD LIKE '%'||#{prodCd}||'%'
AND tmps2.PROD_NM LIKE '%'||#{prodNm}||'%'
AND
tmps2.PROD_CD = #{item}
AND tmps2.HQ_BRAND_CD = #{prodHqBrandCd} -- 상품브랜드 선택일때
-- 상품브랜드 전체일때 AND tmps2.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tsmps.SEL_TYPE_FG IN ('N', 'P')
AND tsmps.SEL_TYPE_FG IN ('N', 'S')
AND tms.STORE_CD = tsmps.STORE_CD AND tmsi.STORE_CD (+)= tms.STORE_CD AND tmp.STORE_CD = tsmps.STORE_CD AND tmp.PROD_CD = tsmps.PROD_CD AND tmps2.STORE_CD = tsmps.STORE_CD AND tmps2.PROD_CD = tsmps.PROD_CD AND tmpcv.STORE_CD = tmps2.STORE_CD AND tmpcv.PROD_CD = tmps2.PROD_CD
GROUP BY tsmps.SALE_YM, L_CLASS_CD, L_CLASS_NM, M_CLASS_CD, M_CLASS_NM, S_CLASS_CD, S_CLASS_NM, tsmps.PROD_CD, tmp.PROD_NM, tsmps.SEL_TYPE_FG, tsmps.SIDE_PROD_CD, tmps2.PROD_NM ORDER BY tsmps.SALE_YM, tsmps.SIDE_PROD_CD, (CASE tsmps.SEL_TYPE_FG WHEN 'S' THEN tsmps.PROD_CD END) DESC
GROUP BY L_CLASS_CD, L_CLASS_NM, M_CLASS_CD, M_CLASS_NM, S_CLASS_CD, S_CLASS_NM, tsmps.PROD_CD, tmp.PROD_NM, tsmps.SEL_TYPE_FG, tsmps.SIDE_PROD_CD, tmps2.PROD_NM ORDER BY tsmps.SIDE_PROD_CD, (CASE tsmps.SEL_TYPE_FG WHEN 'S' THEN tsmps.PROD_CD END) DESC
