mapper.select:
/* MonthProdMapper.getMonthProdExcelList */ SELECT TO_CHAR(TO_DATE(tsmps.SALE_YM, 'YYYYMM'), 'YYYY-MM') AS SALE_YM , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tsmps.STORE_CD , tms.STORE_NM , FN_GET_PROD_CLASS_CD_NM_STR(thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM , tsmps.PROD_CD , CASE tsmps.SEL_TYPE_FG WHEN 'S' THEN tsmps.SIDE_PROD_CD END AS SIDE_PROD_CD , CASE tsmps.SEL_TYPE_FG WHEN 'P' THEN '사이드모상품' WHEN 'S' THEN '사이드상품' WHEN 'N' THEN '일반판매상품' END AS SEL_TYPE_FG , thp.PROD_NM , CASE tsmps.SEL_TYPE_FG WHEN 'S' THEN 'ㅤ- ▶ ' || thps.PROD_NM ELSE thps.PROD_NM END AS SIDE_PROD_NM , SUM(CASE WHEN tsmps.SEL_TYPE_FG = 'N' OR tsmps.SEL_TYPE_FG = 'P' THEN tsmps.TOT_SALE_QTY END) AS SALE_QTY1 , SUM(CASE WHEN tsmps.SEL_TYPE_FG = 'N' OR tsmps.SEL_TYPE_FG = 'S' THEN tsmps.TOT_SALE_QTY END) AS SALE_QTY2 , SUM(tsmps.TOT_SALE_QTY) AS SALE_QTY3 , SUM(tsmps.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(tsmps.TOT_DC_AMT) AS DC_AMT , SUM(CASE WHEN tsmps.SEL_TYPE_FG = 'N' OR tsmps.SEL_TYPE_FG = 'P' THEN tsmps.REAL_SALE_AMT END) AS REAL_SALE_AMT1 , SUM(CASE WHEN tsmps.SEL_TYPE_FG = 'N' OR tsmps.SEL_TYPE_FG = 'S' THEN tsmps.REAL_SALE_AMT END) AS REAL_SALE_AMT2 , SUM(tsmps.REAL_SALE_AMT) AS REAL_SALE_AMT3 FROM TB_SL_MONTHLY_PROD_SDSEL tsmps , TB_HQ_PRODUCT thp , TB_HQ_PRODUCT thps , TB_MS_STORE tms WHERE tsmps.HQ_OFFICE_CD = #{hqOfficeCd} AND tsmps.SALE_YM BETWEEN #{startMonth} AND #{endMonth} AND thp.HQ_OFFICE_CD = tsmps.HQ_OFFICE_CD AND thp.PROD_CD = tsmps.PROD_CD AND thps.HQ_OFFICE_CD = tsmps.HQ_OFFICE_CD AND thps.PROD_CD = tsmps.SIDE_PROD_CD AND tsmps.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsmps.STORE_CD = tms.STORE_CD
AND tsmps.STORE_CD IN
#{item}
AND tsmps.SIDE_PROD_CD LIKE '%'|| #{prodCd} ||'%'
AND thps.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd})
AND tsmps.HQ_BRAND_CD = #{hqBrandCd}
AND tsmps.SEL_TYPE_FG IN ('N', 'P')
AND tsmps.SEL_TYPE_FG IN ('N', 'S')
GROUP BY TSMPS.SALE_YM, tms.HQ_OFFICE_CD, tms.BRANCH_CD, tsmps.STORE_CD, tms.STORE_NM, thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, tsmps.PROD_CD, thp.PROD_NM, tsmps.SEL_TYPE_FG, tsmps.SIDE_PROD_CD, thps.PROD_NM
