mapper.select:
/* MonthSalesVolumeMapper.getMonthSalesVolumeList */
SELECT TO_CHAR(TO_DATE(tsdpt.SALE_DATE, 'YYYYMMDD'), 'YYYYMM') AS SALE_YM , #{startTime} || '~' || #{endTime} || '시' AS SALE_HOUR , FN_GET_PROD_CLASS_CD_NM_STR(thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM , thp.PROD_NM , tsdpt.PROD_CD , SUM(tsdpt.TOT_SALE_QTY) AS TOT_SALE_QTY , SUM(tsdpt.TOT_SALE_AMT) AS TOT_SALE_AMT
FROM TB_SL_DAILY_PROD_TIME tsdpt , TB_HQ_PRODUCT thp WHERE thp.HQ_OFFICE_CD = tsdpt.HQ_OFFICE_CD AND thp.PROD_CD = tsdpt.PROD_CD AND tsdpt.HQ_OFFICE_CD = #{hqOfficeCd} AND SALE_DATE BETWEEN #{startMonth} || '01' AND #{startMonth} || '31' AND SALE_HOUR BETWEEN #{startTime} AND #{endTime}
AND tsdpt.STORE_CD IN
#{item}
AND thp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
GROUP BY TO_CHAR(TO_DATE(tsdpt.SALE_DATE, 'YYYYMMDD'), 'YYYYMM'), thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, thp.PROD_NM, tsdpt.PROD_CD ORDER BY TO_CHAR(TO_DATE(tsdpt.SALE_DATE, 'YYYYMMDD'), 'YYYYMM'), tsdpt.PROD_CD

/* MonthSalesVolumeMapper.getMonthSalesVolumeExcelList */ SELECT TO_CHAR(TO_DATE(tsdpt.SALE_DATE, 'YYYYMMDD'), 'YYYYMM') AS SALE_YM , #{startTime} || '~' || #{endTime} || '시' AS SALE_HOUR , FN_GET_PROD_CLASS_CD_NM_STR(thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM , thp.PROD_NM , tsdpt.PROD_CD , SUM(tsdpt.TOT_SALE_QTY) AS TOT_SALE_QTY , SUM(tsdpt.TOT_SALE_AMT) AS TOT_SALE_AMT
FROM TB_SL_DAILY_PROD_TIME tsdpt , TB_HQ_PRODUCT thp WHERE thp.HQ_OFFICE_CD = tsdpt.HQ_OFFICE_CD AND thp.PROD_CD = tsdpt.PROD_CD AND tsdpt.HQ_OFFICE_CD = #{hqOfficeCd} AND SALE_DATE BETWEEN #{startMonth} || '01' AND #{startMonth} || '31' AND SALE_HOUR BETWEEN #{startTime} AND #{endTime}
AND tsdpt.STORE_CD IN
#{item}
AND thp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
GROUP BY TO_CHAR(TO_DATE(tsdpt.SALE_DATE, 'YYYYMMDD'), 'YYYYMM'), thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, thp.PROD_NM, tsdpt.PROD_CD ORDER BY TO_CHAR(TO_DATE(tsdpt.SALE_DATE, 'YYYYMMDD'), 'YYYYMM'), tsdpt.PROD_CD
