mapper.select:
/* PeriodProdMapper.getPeriodProdList */
SELECT A.PROD_CD , A.TOT_SALE_QTY , A.TOT_SALE_AMT , A.REAL_SALE_AMT , ROUND(RATIO_TO_REPORT(A.TOT_SALE_QTY) OVER(), 4) * 100 AS RATE , B.TOT_SALE_QTY AS TOT_SALE_QTY1 , B.TOT_SALE_AMT AS TOT_SALE_AMT1 , B.REAL_SALE_AMT AS REAL_SALE_AMT1 , ROUND(RATIO_TO_REPORT(B.TOT_SALE_QTY) OVER(), 4) * 100 AS RATE1 , DECODE(B.TOT_SALE_QTY, 0, 0, ROUND(A.TOT_SALE_QTY/B.TOT_SALE_QTY * 100), 2) AS RATE_SALE_QTY , DECODE(B.TOT_SALE_AMT, 0, 0, ROUND(A.TOT_SALE_AMT/B.TOT_SALE_AMT * 100), 2) AS RATE_SALE_AMT , DECODE(B.REAL_SALE_AMT, 0, 0, ROUND(A.REAL_SALE_AMT/B.REAL_SALE_AMT * 100), 2) AS RATE_REAL_SALE_AMT
FROM ( SELECT tsdp.PROD_CD , tsdp.TOT_SALE_QTY , tsdp.TOT_SALE_AMT , tsdp.REAL_SALE_AMT FROM TB_SL_DAILY_PROD tsdp , TB_HQ_PRODUCT thp WHERE tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.HQ_OFFICE_CD = tsdp.HQ_OFFICE_CD AND thp.PROD_CD = tsdp.PROD_CD AND SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND STORE_CD IN
#{item}
AND thp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
) A , ( SELECT tsdp.PROD_CD , tsdp.TOT_SALE_QTY , tsdp.TOT_SALE_AMT , tsdp.REAL_SALE_AMT FROM TB_SL_DAILY_PROD tsdp , TB_HQ_PRODUCT thp WHERE tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.HQ_OFFICE_CD = tsdp.HQ_OFFICE_CD AND thp.PROD_CD = tsdp.PROD_CD AND SALE_DATE BETWEEN #{compStartDate} AND #{compEndDate}
AND STORE_CD IN
#{item}
AND thp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
) B WHERE A.PROD_CD = B.PROD_CD(+) ORDER BY A.PROD_CD

