mapper.select:
/* ProdStoreMapper.getProdStoreList */
SELECT #{startDate} AS START_DATE , #{endDate} AS END_DATE , tsdp.STORE_CD , tms.STORE_NM , FN_GET_PROD_CLASS_CD_NM_STR(thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM , tsdp.PROD_CD , thp.PROD_NM , SUM(tsdp.TOT_SALE_QTY) AS SALE_QTY , SUM(tsdp.TOT_SALE_AMT) AS TOT_SALE_AMT
FROM TB_SL_DAILY_PROD tsdp , TB_HQ_PRODUCT thp , TB_MS_STORE tms WHERE thp.HQ_OFFICE_CD = tsdp.HQ_OFFICE_CD AND thp.PROD_CD = tsdp.PROD_CD AND tms.HQ_OFFICE_CD = tsdp.HQ_OFFICE_CD AND tms.STORE_CD = tsdp.STORE_CD AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{startDate}
AND tsdp.STORE_CD IN
#{item}
AND thp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
AND tsdp.HQ_BRAND_CD = #{hqBrandCd}
GROUP BY thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, tsdp.STORE_CD, tms.STORE_NM, tsdp.PROD_CD, thp.PROD_NM ORDER BY thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD

/* ProdStoreMapper.getProdStoreExcelList */ SELECT #{startDate} AS START_DATE , #{endDate} AS END_DATE , tsdp.STORE_CD , tms.STORE_NM , FN_GET_PROD_CLASS_CD_NM_STR(thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM , tsdp.PROD_CD , thp.PROD_NM , SUM(tsdp.TOT_SALE_QTY) AS SALE_QTY , SUM(tsdp.TOT_SALE_AMT) AS TOT_SALE_AMT FROM TB_SL_DAILY_PROD tsdp , TB_HQ_PRODUCT thp , TB_MS_STORE tms WHERE thp.HQ_OFFICE_CD = tsdp.HQ_OFFICE_CD AND thp.PROD_CD = tsdp.PROD_CD AND tms.HQ_OFFICE_CD = tsdp.HQ_OFFICE_CD AND tms.STORE_CD = tsdp.STORE_CD AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{startDate}
AND tsdp.STORE_CD IN
#{item}
AND thp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND thp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
AND tsdp.HQ_BRAND_CD = #{hqBrandCd}
GROUP BY thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, tsdp.STORE_CD, tms.STORE_NM, tsdp.PROD_CD, thp.PROD_NM ORDER BY thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD
