SELECT M.STORE_CD , TMS.STORE_NM , M.ACQUIRE_CD , M.ACQUIRE_NM , SUM(M.CNT) AS CNT, SUM(M.SALE_AMT) AS SALE_AMT, SUM(M.APPR_AMT) AS APPR_AMT --전체 , SUM(M.CNT_A) AS CNT_A, SUM(M.SALE_AMT_A) AS SALE_AMT_A, SUM(M.APPR_AMT_A) AS APPR_AMT_A --승인 , SUM(M.CNT_B) AS CNT_B, SUM(M.SALE_AMT_B) AS SALE_AMT_B, SUM(M.APPR_AMT_B) AS APPR_AMT_B --취소
FROM TB_MS_STORE TMS , ( SELECT M.STORE_CD , M.ACQUIRE_CD , M.ACQUIRE_NM , SUM(M.CNT) AS CNT , SUM(M.SALE_AMT) AS SALE_AMT , SUM(M.APPR_AMT) AS APPR_AMT , DECODE(M.SALE_FG,1,SUM(M.CNT),0) AS CNT_A , DECODE(M.SALE_FG,1,SUM(M.SALE_AMT),0) AS SALE_AMT_A , DECODE(M.SALE_FG,1,SUM(M.APPR_AMT),0) AS APPR_AMT_A , DECODE(M.SALE_FG,-1,SUM(M.CNT),0) AS CNT_B , DECODE(M.SALE_FG,-1,SUM(M.SALE_AMT),0) AS SALE_AMT_B , DECODE(M.SALE_FG,-1,SUM(M.APPR_AMT),0) AS APPR_AMT_B FROM ( SELECT STORE_CD , ACQUIRE_CD , ACQUIRE_NM , SALE_FG , 1 AS CNT , SALE_AMT , APPR_AMT FROM TB_SL_SALE_PAY_CARD WHERE SALE_FG='1' AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD IN
#{item}
AND STORE_CD||'||'||CORNR_CD IN
#{item}
AND STORE_CD||'||'||POS_NO IN
#{item}
AND APPR_PROC_FG = #{apprProcFg}
AND SALE_DATE >= #{startDate}
AND SALE_DATE <= #{endDate}
UNION ALL SELECT STORE_CD , ACQUIRE_CD , ACQUIRE_NM , SALE_FG , 1 AS CNT , SALE_AMT , APPR_AMT FROM TB_SL_SALE_PAY_CARD WHERE SALE_FG='-1' AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD IN
#{item}
AND STORE_CD||'||'||CORNR_CD IN
#{item}
AND STORE_CD||'||'||POS_NO IN
#{item}
AND APPR_PROC_FG = #{apprProcFg}
AND SALE_DATE >= #{startDate}
AND SALE_DATE <= #{endDate}
) M GROUP BY M.STORE_CD, M.SALE_FG, M.ACQUIRE_CD, M.ACQUIRE_NM
HAVING(M.SALE_FG = #{saleFg})
) M WHERE M.STORE_CD = TMS.STORE_CD GROUP BY M.STORE_CD, TMS.STORE_NM, M.ACQUIRE_CD, M.ACQUIRE_NM ORDER BY M.STORE_CD, M.ACQUIRE_CD, M.ACQUIRE_NM

SELECT M.STORE_CD , TMS.STORE_NM , M.MCOUPN_CD , M.MCOUPN_NM , SUM(M.CNT) AS CNT, SUM(M.APPR_AMT) AS APPR_AMT , SUM(M.CNT_A) AS CNT_A, SUM(M.APPR_AMT_A) AS APPR_AMT_A , SUM(M.CNT_B) AS CNT_B, SUM(M.APPR_AMT_B) AS APPR_AMT_B
FROM TB_MS_STORE TMS , ( SELECT M.STORE_CD , M.MCOUPN_CD , M.MCOUPN_NM , SUM(M.CNT) AS CNT , SUM(M.SALE_AMT) AS APPR_AMT , DECODE(M.SALE_FG,1,SUM(M.CNT),0) AS CNT_A , DECODE(M.SALE_FG,1,SUM(M.SALE_AMT),0) AS APPR_AMT_A , DECODE(M.SALE_FG,-1,SUM(M.CNT),0) AS CNT_B , DECODE(M.SALE_FG,-1,SUM(M.SALE_AMT),0) AS APPR_AMT_B FROM ( SELECT TSSPM.STORE_CD , A.VAN_CD MCOUPN_CD , A.VAN_NM MCOUPN_NM , TSSPM.SALE_FG , 1 AS CNT , TSSPM.SALE_AMT FROM TB_SL_SALE_PAY_MCOUPN TSSPM , (SELECT VAN_NM, VAN_CD FROM TB_CM_VAN_CMPNY WHERE VAN_FG = '04') A WHERE TSSPM.SALE_FG = 1 AND A.VAN_CD = TSSPM.MCOUPN_CD AND TSSPM.HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD IN
#{item}
AND STORE_CD||'||'||CORNR_CD IN
#{item}
AND STORE_CD||'||'||POS_NO IN
#{item}
AND CASH_BILL_APPR_PROC_FG = #{cashBillApprProcFg}
AND SALE_DATE >= #{startDate}
AND SALE_DATE <= #{endDate}
UNION ALL SELECT TSSPM.STORE_CD , A.VAN_CD MCOUPN_CD , A.VAN_NM MCOUPN_NM , TSSPM.SALE_FG , 1 AS CNT , TSSPM.SALE_AMT FROM TB_SL_SALE_PAY_MCOUPN TSSPM , (SELECT VAN_NM, VAN_CD FROM TB_CM_VAN_CMPNY WHERE VAN_FG = '04') A WHERE TSSPM.SALE_FG = -1 AND A.VAN_CD = TSSPM.MCOUPN_CD AND TSSPM.HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD IN
#{item}
AND STORE_CD||'||'||CORNR_CD IN
#{item}
AND STORE_CD||'||'||POS_NO IN
#{item}
AND CASH_BILL_APPR_PROC_FG = #{cashBillApprProcFg}
AND SALE_DATE >= #{startDate}
AND SALE_DATE <= #{endDate}
) M GROUP BY M.STORE_CD, M.SALE_FG, M.MCOUPN_CD, M.MCOUPN_NM
HAVING(M.SALE_FG = #{saleFg})
) M WHERE M.STORE_CD = TMS.STORE_CD GROUP BY M.STORE_CD, TMS.STORE_NM, M.MCOUPN_CD, M.MCOUPN_NM ORDER BY M.STORE_CD, M.MCOUPN_CD

