AND STORE_CD IN
#{item}
AND STORE_CD||'||'||POS_NO IN
#{item}
AND APPR_PROC_FG = #{apprProcFg}
AND APPR_PROC_FG IN ('1','2')
AND SALE_DATE >= #{startDate}
AND SALE_DATE <= #{endDate}
UNION ALL SELECT STORE_CD , SALE_YN , -1 AS CNT , SALE_AMT FROM TB_NS_NONSALE_PAY_CASH WHERE SALE_YN = 'N' AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD IN
#{item}
AND STORE_CD||'||'||POS_NO IN
#{item}
AND APPR_PROC_FG = #{apprProcFg}
AND APPR_PROC_FG IN ('1','2')
AND SALE_DATE >= #{startDate}
AND SALE_DATE <= #{endDate}
) M GROUP BY M.STORE_CD, M.SALE_YN
HAVING(M.SALE_YN = #{saleYn})
) M WHERE M.STORE_CD = TMS.STORE_CD GROUP BY M.STORE_CD, TMS.STORE_NM ORDER BY M.STORE_CD
