mapper.select:
SELECT M.STORE_CD , TMS.STORE_NM , SUM(M.CNT) AS CNT, SUM(M.APPR_AMT) AS APPR_AMT --전체 , SUM(M.CNT_A) AS CNT_A, SUM(M.APPR_AMT_A) AS APPR_AMT_A --승인 , SUM(M.CNT_B) AS CNT_B, SUM(M.APPR_AMT_B) AS APPR_AMT_B --취소
FROM TB_MS_STORE TMS , ( SELECT M.STORE_CD , SUM(M.CNT) AS CNT , SUM(M.SALE_AMT) AS APPR_AMT , DECODE(M.SALE_YN,'Y',SUM(M.CNT),0) AS CNT_A , DECODE(M.SALE_YN,'Y',SUM(M.SALE_AMT),0) AS APPR_AMT_A , DECODE(M.SALE_YN,'N',SUM(M.CNT),0) AS CNT_B , DECODE(M.SALE_YN,'N',SUM(M.SALE_AMT),0) AS APPR_AMT_B FROM ( SELECT STORE_CD , SALE_YN , 1 AS CNT , SALE_AMT FROM TB_NS_NONSALE_PAY_CASH WHERE SALE_YN = 'Y' AND APPR_PROC_FG IN ('1','2') AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD IN
#{item}
AND SALE_DATE >= #{startDate}
AND SALE_DATE <= #{endDate}
UNION ALL SELECT STORE_CD , SALE_YN , 1 AS CNT , SALE_AMT FROM TB_NS_NONSALE_PAY_CASH WHERE SALE_YN = 'N' AND APPR_PROC_FG IN ('1','2') AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD IN
#{item}
AND SALE_DATE >= #{startDate}
AND SALE_DATE <= #{endDate}
) M GROUP BY M.STORE_CD, M.SALE_YN ) M WHERE M.STORE_CD = TMS.STORE_CD GROUP BY M.STORE_CD, TMS.STORE_NM ORDER BY M.STORE_CD
