mapper.select:
SELECT M.STORE_CD , TMS.STORE_NM , SUM(M.CNT) AS CNT, SUM(M.DC_AMT) AS DC_AMT, SUM(M.APPR_AMT) AS APPR_AMT --전체 , SUM(M.CNT_A) AS CNT_A, SUM(M.DC_AMT_A) AS DC_AMT_A, SUM(M.APPR_AMT_A) AS APPR_AMT_A --승인 , SUM(M.CNT_B) AS CNT_B, SUM(M.DC_AMT_B) AS DC_AMT_B, SUM(M.APPR_AMT_B) AS APPR_AMT_B --취소
FROM TB_MS_STORE TMS , ( SELECT M.STORE_CD , SUM(M.CNT) AS CNT , SUM(M.DC_AMT) AS DC_AMT , SUM(M.SALE_AMT) AS APPR_AMT , DECODE(M.SALE_FG,1,SUM(M.CNT),0) AS CNT_A , DECODE(M.SALE_FG,1,SUM(M.DC_AMT),0) AS DC_AMT_A , DECODE(M.SALE_FG,1,SUM(M.SALE_AMT),0) AS APPR_AMT_A , DECODE(M.SALE_FG,-1,SUM(M.CNT),0) AS CNT_B , DECODE(M.SALE_FG,-1,SUM(M.DC_AMT),0) AS DC_AMT_B , DECODE(M.SALE_FG,-1,SUM(M.SALE_AMT),0) AS APPR_AMT_B FROM ( SELECT STORE_CD , SALE_FG , 1 AS CNT , DC_AMT , SALE_AMT FROM TB_SL_SALE_PAY_PARTNER WHERE SALE_FG = 1 AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD IN
#{item}
AND STORE_CD||'||'||CORNR_CD IN
#{item}
AND STORE_CD||'||'||POS_NO IN
#{item}
AND APPR_PROC_FG = #{apprProcFg}
AND SALE_DATE >= #{startDate}
AND SALE_DATE <= #{endDate}
UNION ALL SELECT STORE_CD , SALE_FG , 1 AS CNT , DC_AMT , SALE_AMT FROM TB_SL_SALE_PAY_PARTNER WHERE SALE_FG = -1 AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD IN
#{item}
AND STORE_CD||'||'||CORNR_CD IN
#{item}
AND STORE_CD||'||'||POS_NO IN
#{item}
AND APPR_PROC_FG = #{apprProcFg}
AND SALE_DATE >= #{startDate}
AND SALE_DATE <= #{endDate}
) M GROUP BY M.STORE_CD, M.SALE_FG
HAVING(M.SALE_FG = #{saleFg})
) M WHERE M.STORE_CD = TMS.STORE_CD GROUP BY M.STORE_CD, TMS.STORE_NM ORDER BY M.STORE_CD
