mapper.select:


SELECT TO_CHAR(TO_DATE(TSDCP.SALE_DATE, 'YYYYMMDD'),'YYYY-MM-DD') SALE_DATE  
       ,TO_CHAR(TO_DATE(TSDCP.SALE_DATE, 'yyyymmdd'),'DY') yoil
       ,NVL(SUM(TSDCP.REAL_SALE_AMT),'0') TOT_REAL_SALE_AMT
       ,NVL(SUM(TSDCP.TOT_SALE_QTY),'0') TOT_SALE_QTY
       ${sQuery1}



 FROM TB_SL_DAILY_CORNR_PROD TSDCP
 WHERE TSDCP.HQ_OFFICE_CD = #{hqOfficeCd}


    AND TSDCP.STORE_CD IN

         #{item}



     AND TSDCP.STORE_CD||'||'||TSDCP.CORNR_CD IN

         #{item}



    AND TSDCP.SALE_DATE >= #{startDate}


    AND TSDCP.SALE_DATE <=  #{endDate}


 GROUP BY TSDCP.SALE_DATE, TO_CHAR(TO_DATE(TSDCP.SALE_DATE, 'yyyymmdd'),'DY')
 ORDER BY TSDCP.SALE_DATE DESC

SELECT TMC.STORE_CD||'||'||TMC.CORNR_CD AS CORNR_CD , TMC.CORNR_NM , NVL((SELECT STORE_NM FROM TB_MS_STORE WHERE STORE_CD = TMC.STORE_CD), TMC.STORE_CD) AS STORE_NM FROM TB_MS_CORNER TMC WHERE 1 = 1 AND EXISTS (SELECT * FROM TB_MS_STORE Z WHERE Z.STORE_CD = TMC.STORE_CD AND Z.HQ_OFFICE_CD = #{hqOfficeCd})
AND TMC.STORE_CD IN
#{item}
AND TMC.STORE_CD||'||'||TMC.CORNR_CD IN
#{item}
ORDER BY TMC.STORE_CD||'||'||TMC.CORNR_CD
