mapper.select:
        /* USE : CornerDayPeriodMapper.getCornerDayPeriodList */

             SELECT TSDCP.CORNR_CD
                    , TSDCP.STORE_CD
			        , CONCAT(CONCAT(TMS.STORE_NM,'- '), TMC.CORNR_NM) CORNR_NM 



			        , SUM(TSDCP.TOT_SALE_QTY) AS TOT_SALE_QTY
			        , SUM(TSDCP.REAL_SALE_AMT) AS REAL_SALE_AMT


         FROM TB_MS_CORNER TMC
              , TB_SL_DAILY_CORNR_PROD TSDCP 
              , TB_MS_STORE TMS
         WHERE TMC.STORE_CD = TSDCP.STORE_CD
          AND TMC.CORNR_CD = TSDCP.CORNR_CD
          AND TSDCP.HQ_OFFICE_CD = #{hqOfficeCd} 
          AND TSDCP.STORE_CD = TMS.STORE_CD

           AND TSDCP.STORE_CD IN

                #{item}



            AND TSDCP.STORE_CD||'||'||TSDCP.CORNR_CD IN

                #{item}



           AND TSDCP.SALE_DATE >= #{startDate}


           AND TSDCP.SALE_DATE <=  #{endDate}

 		GROUP BY TSDCP.CORNR_CD, TSDCP.STORE_CD, TMC.CORNR_NM, CONCAT(CONCAT(TMS.STORE_NM,'- '), TMC.CORNR_NM)

/* USE : CornerDayPeriodMapper.getCornerDayPeriodDtlList */
SELECT A.LV1_CD ,A.LV1_NM ,A.LV2_CD ,A.LV2_NM ,A.LV2_NM_FULL ,A.LV3_CD ,A.LV3_NM ,A.LV3_NM_FULL ,C.PROD_CD ,B.PROD_NM ,SUM(C.TOT_SALE_QTY) AS TOT_SALE_QTY ,SUM(C.REAL_SALE_AMT) AS REAL_SALE_AMT
FROM ( SELECT A.P_PROD_CLASS_CD AS LV1_CD, A.PROD_CLASS_CD AS LV2_CD, A.PROD_CLASS_NM AS LV2_NM, B.PROD_CLASS_CD AS LV3_CD, B.PROD_CLASS_NM AS LV3_NM, (SELECT SF_GET_PROD_CLASSES('H', #{hqOfficeCd}, A.P_PROD_CLASS_CD) FROM DUAL) AS LV1_NM, (SELECT SF_GET_PROD_CLASSES('H', #{hqOfficeCd}, A.PROD_CLASS_CD ) FROM DUAL) AS LV2_NM_FULL, (SELECT SF_GET_PROD_CLASSES('H', #{hqOfficeCd}, B.PROD_CLASS_CD ) FROM DUAL) AS LV3_NM_FULL FROM ( SELECT HQ_OFFICE_CD, P_PROD_CLASS_CD, PROD_CLASS_CD, PROD_CLASS_NM FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND P_PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND P_PROD_CLASS_CD = '00000' ) ) A, TB_HQ_PRODUCT_CLASS B WHERE B.P_PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND P_PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND P_PROD_CLASS_CD = '00000' ) ) AND A.HQ_OFFICE_CD = B.HQ_OFFICE_CD AND A.PROD_CLASS_CD = B.P_PROD_CLASS_CD ) A, TB_HQ_PRODUCT B, TB_SL_DAILY_CORNR_PROD C WHERE B.HQ_OFFICE_CD = #{hqOfficeCd} AND B.PROD_CLASS_CD = A.LV3_CD AND B.PROD_CD = C.PROD_CD AND C.HQ_OFFICE_CD = #{hqOfficeCd}
FROM ( SELECT A.P_PROD_CLASS_CD AS LV1_CD, A.PROD_CLASS_CD AS LV2_CD, A.PROD_CLASS_NM AS LV2_NM, B.PROD_CLASS_CD AS LV3_CD, B.PROD_CLASS_NM AS LV3_NM, (SELECT SF_GET_PROD_CLASSES('S', #{storeCd}, A.P_PROD_CLASS_CD) FROM DUAL) AS LV1_NM, (SELECT SF_GET_PROD_CLASSES('S', #{storeCd}, A.PROD_CLASS_CD ) FROM DUAL) AS LV2_NM_FULL, (SELECT SF_GET_PROD_CLASSES('S', #{storeCd}, B.PROD_CLASS_CD ) FROM DUAL) AS LV3_NM_FULL FROM ( SELECT STORE_CD, P_PROD_CLASS_CD, PROD_CLASS_CD, PROD_CLASS_NM FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = #{storeCd} AND P_PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = #{storeCd} AND P_PROD_CLASS_CD = '00000' ) ) A, TB_MS_PRODUCT_CLASS B WHERE B.P_PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = #{storeCd} AND P_PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = #{storeCd} AND P_PROD_CLASS_CD = '00000' ) ) AND A.STORE_CD = B.STORE_CD AND A.PROD_CLASS_CD = B.P_PROD_CLASS_CD ) A, TB_MS_PRODUCT B, TB_SL_DAILY_CORNR_PROD C WHERE B.STORE_CD = #{storeCd} AND B.PROD_CLASS_CD = A.LV3_CD AND B.PROD_CD = C.PROD_CD AND C.HQ_OFFICE_CD = #{hqOfficeCd}
AND C.STORE_CD = #{storeCd}
AND C.CORNR_CD = #{cornrCd}
AND C.SALE_DATE >= #{startDate}
AND C.SALE_DATE <= #{endDate}
GROUP BY A.LV1_CD, A.LV2_CD, A.LV2_NM, A.LV3_CD, A.LV3_NM, A.LV1_NM, A.LV2_NM_FULL, A.LV3_NM_FULL, C.PROD_CD, B.PROD_NM ORDER BY LV1_CD, LV2_CD, LV3_CD, PROD_CD
