mapper.select:
/* USE : CornerDayPeriodMapper.getCornerDayPeriodList */
SELECT TSDCP.CORNR_CD , TSDCP.STORE_CD , ('[' || TMS.STORE_CD || ']' ||TMS.STORE_NM || '-[' || TMC.CORNR_CD || ']' || TMC.CORNR_NM) AS CORNR_NM
, SUM(TSDCP.TOT_SALE_QTY) AS TOT_SALE_QTY , SUM(TSDCP.REAL_SALE_AMT) AS REAL_SALE_AMT
FROM TB_MS_CORNER TMC , TB_SL_DAILY_CORNR TSDCP , TB_MS_STORE TMS WHERE TMC.STORE_CD = TSDCP.STORE_CD AND TMC.CORNR_CD = TSDCP.CORNR_CD AND TSDCP.HQ_OFFICE_CD = #{hqOfficeCd} AND TSDCP.STORE_CD = TMS.STORE_CD
AND TSDCP.STORE_CD IN (${storeCdQuery})
AND TSDCP.STORE_CD||'||'||TSDCP.CORNR_CD IN
#{item}
AND TSDCP.SALE_DATE >= #{startDate}
AND TSDCP.SALE_DATE <= #{endDate}
GROUP BY TSDCP.CORNR_CD, TSDCP.STORE_CD, TMS.STORE_CD, TMS.STORE_NM, TMC.CORNR_CD, TMC.CORNR_NM

/* USE : CornerDayPeriodMapper.getCornerDayPeriodExcelList */ SELECT TSDCP.CORNR_CD , TSDCP.STORE_CD , ('[' || TMS.STORE_CD || ']' ||TMS.STORE_NM || '-[' || TMC.CORNR_CD || ']' || TMC.CORNR_NM) AS CORNR_NM
, SUM(TSDCP.TOT_SALE_QTY) AS TOT_SALE_QTY , SUM(TSDCP.REAL_SALE_AMT) AS REAL_SALE_AMT
FROM TB_MS_CORNER TMC , TB_SL_DAILY_CORNR TSDCP , TB_MS_STORE TMS WHERE TMC.STORE_CD = TSDCP.STORE_CD AND TMC.CORNR_CD = TSDCP.CORNR_CD AND TSDCP.HQ_OFFICE_CD = #{hqOfficeCd} AND TSDCP.STORE_CD = TMS.STORE_CD
AND TSDCP.STORE_CD IN (${storeCdQuery})
AND TSDCP.STORE_CD||'||'||TSDCP.CORNR_CD IN
#{item}
AND TSDCP.SALE_DATE >= #{startDate}
AND TSDCP.SALE_DATE <= #{endDate}
GROUP BY TSDCP.CORNR_CD, TSDCP.STORE_CD, TMS.STORE_CD, TMS.STORE_NM, TMC.CORNR_CD, TMC.CORNR_NM

/* USE : CornerDayPeriodMapper.getCornerDayPeriodDtlList */
SELECT FN_GET_PROD_CLASS_CD_NM_STR(tmp.STORE_CD, tmp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM, tsdcp.PROD_CD, tmp.PROD_NM, SUM(tsdcp.TOT_SALE_QTY) AS TOT_SALE_QTY, SUM(tsdcp.REAL_SALE_AMT) AS REAL_SALE_AMT
FROM TB_SL_DAILY_CORNR_PROD tsdcp, TB_MS_PRODUCT tmp WHERE 1=1 AND tsdcp.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdcp.STORE_CD = #{storeCd}
AND tsdcp.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdcp.CORNR_CD = #{cornrCd}
AND tmp.STORE_CD = tsdcp.STORE_CD AND tmp.PROD_CD = tsdcp.PROD_CD GROUP BY tmp.STORE_CD, tmp.PROD_CLASS_CD, tsdcp.PROD_CD, tmp.PROD_NM ORDER BY tmp.STORE_CD, tmp.PROD_CLASS_CD, tsdcp.PROD_CD

/* USE : CornerDayPeriodMapper.getCornerDayPeriodDtlExcelList */ SELECT FN_GET_PROD_CLASS_CD_NM_STR(tmp.STORE_CD, tmp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM, tsdcp.PROD_CD, tmp.PROD_NM, SUM(tsdcp.TOT_SALE_QTY) AS TOT_SALE_QTY, SUM(tsdcp.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_DAILY_CORNR_PROD tsdcp, TB_MS_PRODUCT tmp WHERE 1=1 AND tsdcp.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdcp.STORE_CD = #{storeCd}
AND tsdcp.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdcp.CORNR_CD = #{cornrCd}
AND tmp.STORE_CD = tsdcp.STORE_CD AND tmp.PROD_CD = tsdcp.PROD_CD GROUP BY tmp.STORE_CD, tmp.PROD_CLASS_CD, tsdcp.PROD_CD, tmp.PROD_NM ORDER BY tmp.STORE_CD, tmp.PROD_CLASS_CD, tsdcp.PROD_CD
