mapper.select:
/* USE : CornerMonthMapper.getCornerMonthList */
SELECT TO_CHAR(TO_DATE(TSDCP.SALE_YM, 'yyyymm'), 'yyyy-mm') SALE_YM ,SUM(TSDCP.REAL_SALE_AMT) TOT_REAL_SALE_AMT ,SUM(TSDCP.TOT_SALE_QTY) TOT_SALE_QTY ${sQuery1}
FROM TB_SL_MONTHLY_CORNR_PROD TSDCP WHERE TSDCP.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSDCP.STORE_CD IN (${storeCdQuery})
AND TSDCP.STORE_CD||'||'||TSDCP.CORNR_CD IN
#{item}
AND TSDCP.SALE_YM >= TO_CHAR(TO_DATE(#{startDate}, 'YYYYMMDD'),'YYYYMM')
AND TSDCP.SALE_YM <= TO_CHAR(TO_DATE(#{endDate}, 'YYYYMMDD'),'YYYYMM')
GROUP BY TSDCP.SALE_YM ORDER BY TSDCP.SALE_YM DESC

