mapper.select:
/* CupReturnStatusMapper.getCupReturnStatusList */ SELECT TO_CHAR(TO_DATE(A.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , A.REG_SEQ , A.POS_NO , A.BARCODE_ID , A.RETURN_TYPE , TO_CHAR(TO_DATE(A.RETURN_DT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS RETURN_DT , A.RETURN_COST , NVL(B.NMCODE_NM,A.CUP_TYPE) AS CUP_TYPE , TO_CHAR(TO_DATE(A.SEND_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SEND_DATE , TO_CHAR(TO_DATE(A.SEND_DT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS SEND_DT , A.SEND_YN , A.REG_DT , A.REG_ID , A.MOD_DT , A.MOD_ID FROM TB_PS_EXACTCALC_CUPRETURN A , TB_CM_NMCODE B WHERE 1=1 AND B.NMCODE_CD (+) = A.CUP_TYPE AND B.NMCODE_GRP_CD (+) = '240' AND A.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND A.STORE_CD = #{storeCd}
AND A.SEND_DATE BETWEEN #{sendStartDate} AND #{sendEndDate}
AND A.BARCODE_ID LIKE '%'||#{barCd}||'%'
AND A.RETURN_TYPE = #{returnType}
AND A.SEND_YN = #{sendYn}
ORDER BY A.SALE_DATE , A.REG_SEQ
