mapper.select:
/* DaySaleReportMapper.getDaySaleReportList */ SELECT SUBSTR(tmsr.FROM_SALE_DATE, 0, 6) AS SALE_MONTH, tmsr.STORE_CD, tms.STORE_NM, -- '전체매장통합파일' AS PROC_GUBUN, tmsr.PROC_DT, (SELECT USER_NM FROM TB_WB_USER_INFO_DTL_V01 WHERE USER_ID = tmsr.REG_ID) AS USER_NM, tmsr.PROC_FG, (CASE WHEN tmsr.PROC_FG IN ('0','1') THEN '대기' ELSE tmsr.PROC_MSG END) AS PROC_MSG, (CASE WHEN tmsr.PROC_FG = '2' THEN '다운로드' ELSE null END) AS DOWNLOAD, tmsr.FROM_SALE_DATE, tmsr.TO_SALE_DATE, tmsr.FILE_NAME FROM TB_MS_SALE_REPORT tmsr, TB_MS_STORE tms WHERE 1=1 AND tmsr.HQ_OFFICE_CD = #{hqOfficeCd}
AND tmsr.FROM_SALE_DATE >= #{startMonth}||'01' AND tmsr.TO_SALE_DATE <= #{endMonth}||'31'
AND tmsr.REPORT_TYPE = '02' AND tms.HQ_OFFICE_CD = tmsr.HQ_OFFICE_CD AND tms.STORE_CD = tmsr.STORE_CD ORDER BY tmsr.FROM_SALE_DATE DESC, tmsr.STORE_CD

mapper.delete:
/* DaySaleReportMapper.getDaySaleReportDel */
DELETE TB_MS_SALE_REPORT
WHERE 1=1
AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD = #{storeCd}
AND FROM_SALE_DATE = #{fromSaleDate}
AND TO_SALE_DATE = #{toSaleDate}
AND REPORT_TYPE = '02'

mapper.select:
/* DaySaleReportMapper.getDaySaleReportChk */
SELECT
COUNT(tmsr.REPORT_TYPE) AS CNT
FROM TB_MS_SALE_REPORT tmsr
WHERE 1=1
AND tmsr.HQ_OFFICE_CD = #{hqOfficeCd}

    AND tmsr.STORE_CD IN

        #{item}


AND tmsr.FROM_SALE_DATE = #{dataCreateMonth}||'01'
AND tmsr.TO_SALE_DATE = #{dataCreateMonth}||#{dataCreateMonthLastDate}
AND tmsr.REPORT_TYPE = '02'
