/* USE : EmpDayMapper.getEmpDayList */
SELECT A.SALE_DATE , COUNT(DISTINCT A.STORE_CNT) STORE_CNT , TO_CHAR(TO_DATE(A.SALE_DATE, 'YYYYMMDD'),'DY') YOIL , SUM(A.REAL_SALE_AMT_TOT) AS REAL_SALE_AMT_TOT , SUM(A.TOT_BILL_CNT) AS TOT_BILL_CNT ${sQuery1}
FROM ( SELECT TSDE.SALE_DATE , COUNT(DISTINCT TSDE.STORE_CD) STORE_CNT , SUM(TSDE.REAL_SALE_AMT) AS REAL_SALE_AMT_TOT , SUM(TSDE.BILL_CNT) AS TOT_BILL_CNT ${sQuery2} FROM TB_SL_DAILY_EMPLOYEE TSDE WHERE 1 = 1
AND TSDE.STORE_CD IN
#{item}
AND TSDE.SALE_DATE >= #{startDate}
AND TSDE.SALE_DATE <= #{endDate}
GROUP BY TSDE.SALE_DATE, TSDE.EMP_NO, TSDE.STORE_CD ORDER BY TSDE.SALE_DATE ASC ) A WHERE 1 = 1 GROUP BY A.SALE_DATE ORDER BY A.SALE_DATE

/* USE : EmpDayMapper.getEmpMebList */ SELECT TMS.STORE_CD , TMS.STORE_NM||'('||TMS.STORE_CD||')' AS STORE_NM , EMP_NO AS NMCODE_CD , (TMS.STORE_NM||'-'||EMP_NM||'('||EMP_NO||')') AS NMCODE_NM FROM TB_MS_EMPLOYEE TME , TB_MS_STORE TMS WHERE TME.STORE_CD = TMS.STORE_CD AND TME.STORE_CD IN
#{item}
AND EXISTS (SELECT * FROM TB_SL_SALE_HDR Z WHERE Z.store_cd = TME.STORE_CD AND Z.EMP_NO = TME.EMP_NO AND Z.STORE_CD IN
#{item}
AND Z.SALE_DATE >= #{startDate}
AND Z.SALE_DATE <= #{endDate}
)
