AND TMS.STORE_CD IN
#{item}
) AS STORE_CNT , TO_CHAR(TO_DATE(TSDE.SALE_DATE, 'YYYYMMDD'),'DY') AS YOIL , TO_CHAR(TO_DATE(TSDE.SALE_DATE, 'YYYYMMDD'),'D') AS YOIL_NUM , SUM(TSDE.REAL_SALE_AMT) AS REAL_SALE_AMT_TOT , SUM(TSDE.BILL_CNT) AS BILL_CNT_TOT ${sQuery2} FROM TB_SL_DAILY_EMPLOYEE TSDE WHERE 1 = 1
AND TSDE.STORE_CD IN
#{item}
AND TSDE.SALE_DATE >= #{startDate}
AND TSDE.SALE_DATE <= #{endDate}
GROUP BY TSDE.SALE_DATE, TSDE.EMP_NO, TSDE.STORE_CD ) A WHERE 1 = 1 GROUP BY A.YOIL, A.YOIL_NUM, A.STORE_CNT ORDER BY YOIL_NUM ASC

