mapper.select:
/* USE : EmpMonthMapper.getEmpMonthList */
SELECT A.SALE_DATE , COUNT(DISTINCT A.STORE_CD) STORE_CNT , SUM(A.REAL_SALE_AMT_TOT) AS REAL_SALE_AMT_TOT , SUM(A.BILL_CNT_TOT) AS TOT_BILL_CNT ${sQuery1}
FROM ( SELECT TO_CHAR(TO_DATE(TSDE.SALE_DATE), 'YYYY-MM') AS SALE_DATE , TSDE.STORE_CD , SUM(TSDE.REAL_SALE_AMT) AS REAL_SALE_AMT_TOT , SUM(TSDE.BILL_CNT) AS BILL_CNT_TOT ${sQuery2} FROM TB_SL_DAILY_EMPLOYEE TSDE WHERE 1 = 1
AND TSDE.STORE_CD IN
#{item}
AND TO_CHAR(TO_DATE(TSDE.SALE_DATE), 'YYYYMM') >= #{startDate}
AND TO_CHAR(TO_DATE(TSDE.SALE_DATE), 'YYYYMM') <= #{endDate}
GROUP BY TSDE.SALE_DATE, TSDE.EMP_NO, TSDE.STORE_CD ) A WHERE 1 = 1 GROUP BY A.SALE_DATE, A.STORE_CD ORDER BY SALE_DATE ASC

/* USE : EmpMonthMapper.getEmpMebList */ SELECT TMS.STORE_CD , TMS.STORE_NM||'('||TMS.STORE_CD||')' AS STORE_NM , EMP_NO AS NMCODE_CD , (TMS.STORE_NM||'-'||EMP_NM||'('||EMP_NO||')') AS NMCODE_NM FROM TB_MS_EMPLOYEE TME , TB_MS_STORE TMS WHERE TME.STORE_CD = TMS.STORE_CD AND TME.STORE_CD IN
#{item}
AND EXISTS (SELECT * FROM TB_SL_SALE_HDR Z WHERE Z.store_cd = TME.STORE_CD AND Z.EMP_NO = TME.EMP_NO AND Z.STORE_CD IN
#{item}
AND TO_CHAR(TO_DATE(Z.SALE_DATE), 'YYYYMM') >= #{startDate} AND TO_CHAR(TO_DATE(Z.SALE_DATE), 'YYYYMM') <= #{endDate} )
