mapper.select:
/* GiftCalcMapper.getGiftCalcList */
SELECT tms.BRANCH_CD, (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM, tsspgd.STORE_CD, tms.STORE_NM, tms.HQ_BRAND_CD AS BRAND_CD, tmsi.MOMS_TEAM, tmsi.MOMS_AC_SHOP, tsspgd.GIFT_CD, tsspgd.GIFT_NM, SUM(tsspgd.SALE_FG) AS SALE_CNT, SUM(tsspgd.SALE_AMT) AS SALE_AMT, SUM(CASE WHEN tsspgd.SALE_FG = 1 THEN 1 ELSE 0 END) AS APPR_CNT, SUM(CASE WHEN tsspgd.SALE_FG = 1 THEN tsspgd.SALE_AMT ELSE 0 END) AS APPR_AMT, SUM(CASE WHEN tsspgd.SALE_FG = -1 THEN 1 ELSE 0 END) AS CANCEL_CNT, SUM(CASE WHEN tsspgd.SALE_FG = -1 THEN tsspgd.SALE_AMT ELSE 0 END) AS CANCEL_AMT
FROM ( SELECT A.HQ_OFFICE_CD, A.STORE_CD, A.POS_NO, B.GIFT_CD, C.GIFT_NM, A.SALE_FG, B.GIFT_UPRC AS SALE_AMT FROM TB_SL_SALE_PAY_GIFT A, TB_SL_SALE_PAY_GIFT_DTL B, TB_HQ_GIFT C WHERE 1=1 AND A.HQ_OFFICE_CD = #{hqOfficeCd}
AND A.STORE_CD IN (${storeCdQuery})
AND A.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND B.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND B.STORE_CD = A.STORE_CD AND B.SALE_DATE = A.SALE_DATE AND B.POS_NO = A.POS_NO AND B.BILL_NO = A.BILL_NO AND B.LINE_NO = A.LINE_NO AND C.HQ_OFFICE_CD (+)= B.HQ_OFFICE_CD AND C.GIFT_CD (+)= B.GIFT_CD UNION ALL SELECT A.HQ_OFFICE_CD, A.STORE_CD, A.POS_NO, A.BARCD_NO AS GIFT_CD, B.NMCODE_NM AS GIFT_NM, (CASE WHEN A.SALE_YN = 'Y' THEN 1 WHEN A.SALE_YN = 'N' THEN -1 ELSE 0 END) AS SALE_FG, A.SALE_AMT FROM TB_SL_SALE_PAY_TEMPORARY A, TB_CM_NMCODE B WHERE 1=1 AND A.HQ_OFFICE_CD = #{hqOfficeCd}
AND A.STORE_CD IN (${storeCdQuery})
AND A.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND A.TEMPORARY_PAY_DTL_CD = 'VGC' AND B.NMCODE_GRP_CD (+)= '230' AND B.NMCODE_ITEM_1 (+)= A.TEMPORARY_PAY_DTL_CD ) tsspgd, TB_MS_STORE tms, TB_MS_STORE_INFO tmsi WHERE 1=1
AND tsspgd.STORE_CD||'||'||tsspgd.POS_NO IN
#{item}
AND tsspgd.SALE_FG = #{saleFg}
AND tms.HQ_OFFICE_CD = tsspgd.HQ_OFFICE_CD AND tms.STORE_CD = tsspgd.STORE_CD AND tmsi.STORE_CD (+)= tsspgd.STORE_CD
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}
GROUP BY tms.BRANCH_CD, tsspgd.STORE_CD, tms.STORE_NM, tms.HQ_BRAND_CD, tmsi.MOMS_TEAM, tmsi.MOMS_AC_SHOP, tsspgd.GIFT_CD, tsspgd.GIFT_NM ORDER BY tms.BRANCH_CD, tsspgd.STORE_CD

/* GiftCalcMapper.getGiftCalcExcelList */ SELECT tms.BRANCH_CD, (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM, tsspgd.STORE_CD, tms.STORE_NM, tms.HQ_BRAND_CD AS BRAND_CD, tmsi.MOMS_TEAM, tmsi.MOMS_AC_SHOP, tsspgd.GIFT_CD, tsspgd.GIFT_NM, SUM(tsspgd.SALE_FG) AS SALE_CNT, SUM(tsspgd.SALE_AMT) AS SALE_AMT, SUM(CASE WHEN tsspgd.SALE_FG = 1 THEN 1 ELSE 0 END) AS APPR_CNT, SUM(CASE WHEN tsspgd.SALE_FG = 1 THEN tsspgd.SALE_AMT ELSE 0 END) AS APPR_AMT, SUM(CASE WHEN tsspgd.SALE_FG = -1 THEN 1 ELSE 0 END) AS CANCEL_CNT, SUM(CASE WHEN tsspgd.SALE_FG = -1 THEN tsspgd.SALE_AMT ELSE 0 END) AS CANCEL_AMT FROM ( SELECT A.HQ_OFFICE_CD, A.STORE_CD, A.POS_NO, B.GIFT_CD, C.GIFT_NM, A.SALE_FG, B.GIFT_UPRC AS SALE_AMT FROM TB_SL_SALE_PAY_GIFT A, TB_SL_SALE_PAY_GIFT_DTL B, TB_HQ_GIFT C WHERE 1=1 AND A.HQ_OFFICE_CD = #{hqOfficeCd}
AND A.STORE_CD IN (${storeCdQuery})
