AND A.STORE_CD IN (${storeCdQuery})
AND A.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND A.TEMPORARY_PAY_DTL_CD = 'VGC' AND B.NMCODE_GRP_CD (+)= '230' AND B.NMCODE_ITEM_1 (+)= A.TEMPORARY_PAY_DTL_CD ) tsspgd, TB_MS_STORE tms, TB_MS_STORE_INFO tmsi WHERE 1=1
AND tsspgd.STORE_CD||'||'||tsspgd.POS_NO IN
