mapper.select:
/* USE : MCoupnCalcMapper.getMCoupnCalcList */
SELECT tms.BRANCH_CD , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tsspm2.STORE_CD , tms.STORE_NM , tms.HQ_BRAND_CD AS BRAND_CD , tmsi.MOMS_TEAM , tmsi.MOMS_AC_SHOP , tsspm2.MCOUPN_CD , tcvc.MCOUPN_NM , SUM(tsspm2.CNT) AS CNT , SUM(tsspm2.APPR_AMT) AS APPR_AMT , SUM(tsspm2.CNT_OK) AS CNT_OK , SUM(tsspm2.APPR_AMT_OK) AS APPR_AMT_OK , SUM(tsspm2.CNT_CANCEL) AS CNT_CANCEL , SUM(tsspm2.APPR_AMT_CANCEL) AS APPR_AMT_CANCEL
FROM TB_MS_STORE tms , TB_MS_STORE_INFO tmsi , ( SELECT VAN_CD AS MCOUPN_CD, VAN_NM AS MCOUPN_NM FROM TB_CM_VAN_CMPNY WHERE VAN_FG = '04' ) tcvc , ( SELECT tsspm.STORE_CD , SUM(tsspm.CNT) AS CNT , SUM(tsspm.SALE_AMT) AS APPR_AMT , DECODE(tsspm.SALE_FG,1,SUM(tsspm.CNT),0) AS CNT_OK , DECODE(tsspm.SALE_FG,1,SUM(tsspm.SALE_AMT),0) AS APPR_AMT_OK , DECODE(tsspm.SALE_FG,-1,ABS(SUM(tsspm.CNT)),0) AS CNT_CANCEL , DECODE(tsspm.SALE_FG,-1,ABS(SUM(tsspm.SALE_AMT)),0) AS APPR_AMT_CANCEL , tsspm.MCOUPN_CD FROM ( SELECT STORE_CD , SALE_FG , 1 AS CNT , SALE_AMT , MCOUPN_CD FROM TB_SL_SALE_PAY_MCOUPN WHERE SALE_FG = 1 AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND STORE_CD||'||'||POS_NO IN (${posNoQuery})
AND MCOUPN_CD = #{mcoupnCd}
AND CASH_BILL_APPR_PROC_FG = #{cashBillApprProcFg}
AND SALE_DATE >= #{startDate}
AND SALE_DATE <= #{endDate}
UNION ALL SELECT STORE_CD , SALE_FG , -1 AS CNT , SALE_AMT , MCOUPN_CD FROM TB_SL_SALE_PAY_MCOUPN WHERE SALE_FG = -1 AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND STORE_CD||'||'||POS_NO IN (${posNoQuery})
AND MCOUPN_CD = #{mcoupnCd}
AND CASH_BILL_APPR_PROC_FG = #{cashBillApprProcFg}
AND SALE_DATE >= #{startDate}
AND SALE_DATE <= #{endDate}
) tsspm GROUP BY tsspm.STORE_CD , tsspm.SALE_FG , tsspm.MCOUPN_CD
HAVING tsspm.SALE_FG = #{saleFg}
) tsspm2 WHERE tms.STORE_CD = tsspm2.STORE_CD AND tmsi.STORE_CD (+)= tsspm2.STORE_CD AND tcvc.MCOUPN_CD = tsspm2.MCOUPN_CD
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 선택한 매장브랜드가 있을 때
-- 선택한 매장브랜드가 없을 때('전체' 일때), 사용자 관리브랜드에 속한 정보만 조회 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}
AND tmsi.MOMS_STORE_FG_02 = #{momsStoreFg02}
AND tmsi.MOMS_STORE_FG_03 = #{momsStoreFg03}
AND tmsi.MOMS_STORE_FG_04 = #{momsStoreFg04}
AND tmsi.MOMS_STORE_FG_05 = #{momsStoreFg05}
GROUP BY tms.BRANCH_CD , tsspm2.STORE_CD , tms.STORE_NM , tms.HQ_BRAND_CD , tmsi.MOMS_TEAM , tmsi.MOMS_AC_SHOP , tsspm2.MCOUPN_CD , tcvc.MCOUPN_NM ORDER BY tsspm2.STORE_CD

