AND MCOUPN_CD = #{mcoupnCd}
AND CASH_BILL_APPR_PROC_FG = #{cashBillApprProcFg}
AND SALE_DATE >= #{startDate}
AND SALE_DATE <= #{endDate}
UNION ALL SELECT STORE_CD , SALE_FG , -1 AS CNT , SALE_AMT , MCOUPN_CD FROM TB_SL_SALE_PAY_MCOUPN WHERE SALE_FG = -1 AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD IN (${storeCdQuery})
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND STORE_CD||'||'||POS_NO IN (${posNoQuery})
AND MCOUPN_CD = #{mcoupnCd}
AND CASH_BILL_APPR_PROC_FG = #{cashBillApprProcFg}
AND SALE_DATE >= #{startDate}
AND SALE_DATE <= #{endDate}
) tsspm GROUP BY tsspm.STORE_CD , tsspm.SALE_FG , tsspm.MCOUPN_CD
HAVING tsspm.SALE_FG = #{saleFg}
) tsspm2 WHERE tms.STORE_CD = tsspm2.STORE_CD AND tmsi.STORE_CD (+)= tsspm2.STORE_CD AND tcvc.MCOUPN_CD = tsspm2.MCOUPN_CD
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 선택한 매장브랜드가 있을 때
-- 선택한 매장브랜드가 없을 때('전체' 일때), 사용자 관리브랜드에 속한 정보만 조회 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
