mapper.select:
/* NonSaleCardMapper.getNonSaleCardList */ SELECT tnnpc.STORE_CD, tms.STORE_NM, TO_CHAR(TO_DATE(tnnpc.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE, tnnpc.POS_NO, tnnpc.BILL_NO, DECODE(tnnpc.SALE_YN, 'Y', '승인', 'N', '취소') AS APPR_PROC, DECODE(tnnpc.APPR_PROC_FG, 1, '포스승인', 2, '단말기승인') AS APPR_PROC_FG, tnnpc.ACQUIRE_NM, DECODE(tnnpc.CARD_TYPE_FG, 1, '일반', 2, 'PAYON', 3, 'APP', 4, 'UNION', 5, 'PAYCO') AS CARD_TYPE_FG, tnnpc.CARD_NO, tnnpc.SALE_AMT, tnnpc.TIP_AMT, tnnpc.VAT_AMT, DECODE(tnnpc.INST_CNT, 0, '일시불', '할부') AS INST_CNT_NM, tnnpc.INST_CNT, TO_CHAR(TO_DATE(tnnpc.APPR_DT, 'YYYY-MM-DD HH24:MI:SS'), 'YYYY-MM-DD HH24:MI:SS') AS APPR_DT, tnnpc.APPR_NO, (DECODE(tnnpc.SALE_YN, 'Y', 1, 'N', -1) * tnnpc.APPR_AMT) AS APPR_AMT FROM TB_NS_NONSALE_PAY_CARD tnnpc, TB_MS_STORE tms WHERE 1=1 AND tnnpc.HQ_OFFICE_CD = #{hqOfficeCd} AND tnnpc.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND
tnnpc.STORE_CD = #{item}
AND tnnpc.STORE_CD||'||'||tnnpc.POS_NO IN
#{item}
AND tnnpc.SALE_YN = #{saleYn}
AND tnnpc.APPR_PROC_FG = #{apprProcFg}
AND tms.STORE_CD = tnnpc.STORE_CD ORDER BY tnnpc.STORE_CD, tnnpc.SALE_DATE, tnnpc.POS_NO, tnnpc.BILL_NO
