mapper.select:
/** OrderEmpMapper.getOrderEmpPeriodList */
SELECT tssd.STORE_CD, tms.STORE_NM, tssd.ORDER_EMP_NO, tme.EMP_NM, COUNT(tssd.BILL_NO) AS BILL_CNT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT
FROM TB_SL_SALE_DTL tssd, TB_MS_EMPLOYEE tme, TB_MS_STORE tms WHERE tme.STORE_CD = tssd.STORE_CD AND tme.EMP_NO = tssd.ORDER_EMP_NO AND tms.STORE_CD = tssd.STORE_CD AND tssd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tssd.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tssd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tssd.SALE_DATE >= #{startDate}
AND tssd.SALE_DATE <= #{endDate}
GROUP BY tssd.STORE_CD, tms.STORE_NM, tssd.ORDER_EMP_NO, tme.EMP_NM ORDER BY tssd.STORE_CD, tssd.ORDER_EMP_NO

/** OrderEmpMapper.getOrderEmpPeriodExcelList */ SELECT tssd.STORE_CD, tms.STORE_NM, tssd.ORDER_EMP_NO, tme.EMP_NM, COUNT(tssd.BILL_NO) AS BILL_CNT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_SALE_DTL tssd, TB_MS_EMPLOYEE tme, TB_MS_STORE tms WHERE tme.STORE_CD = tssd.STORE_CD AND tme.EMP_NO = tssd.ORDER_EMP_NO AND tms.STORE_CD = tssd.STORE_CD AND tssd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tssd.STORE_CD IN
#{item}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tssd.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tssd.SALE_DATE >= #{startDate}
AND tssd.SALE_DATE <= #{endDate}
GROUP BY tssd.STORE_CD, tms.STORE_NM, tssd.ORDER_EMP_NO, tme.EMP_NM ORDER BY tssd.STORE_CD, tssd.ORDER_EMP_NO

/** OrderEmpMapper.getOrderEmpPeriodDtlList */
SELECT TO_CHAR(TO_DATE(tssd.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , tssd.STORE_CD , tssd.POS_NO , tssd.BILL_NO , DECODE(tssd.SALE_YN,'Y','매출','반품') AS SALE_YN , (SELECT '['||TSSH.TBL_CD||']'||tmt.TBL_NM FROM TB_MS_TABLE tmt, TB_SL_SALE_HDR tssh WHERE tmt.STORE_CD = TSSH.STORE_CD AND tmt.TBL_CD = TSSH.TBL_CD AND TSSH.STORE_CD = tssd.STORE_CD AND TSSH.SALE_DATE = tssd.SALE_DATE AND TSSH.POS_NO = tssd.POS_NO AND TSSH.BILL_NO = tssd.BILL_NO) AS TBL_CD , TO_CHAR(TO_DATE(tssd.BILL_DT, 'YYYYMMDDHH24MISS'), 'yyyy-MM-dd HH:mm:ss') AS BILL_DT , SUM(tssd.SALE_AMT) AS TOT_SALE_AMT , SUM(tssd.DC_AMT) AS TOT_DC_AMT , SUM(tssd.REAL_SALE_AMT) AS REAL_SALE_AMT
FROM TB_SL_SALE_DTL tssd WHERE 1=1 AND tssd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tssd.STORE_CD = #{storeCd}
AND tssd.SALE_DATE >= #{startDate}
AND tssd.SALE_DATE <= #{endDate}
AND tssd.ORDER_EMP_NO = #{empNo} GROUP BY SALE_DATE, STORE_CD, POS_NO, BILL_NO, SALE_YN, BILL_DT ORDER BY tssd.SALE_DATE DESC, tssd.POS_NO, tssd.BILL_NO

SELECT TO_CHAR(TO_DATE(tssd.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , tssd.STORE_CD , tssd.POS_NO , tssd.BILL_NO , DECODE(tssd.SALE_YN,'Y','매출','반품') AS SALE_YN , (SELECT '['||TSSH.TBL_CD||']'||tmt.TBL_NM FROM TB_MS_TABLE tmt, TB_SL_SALE_HDR tssh WHERE tmt.STORE_CD = TSSH.STORE_CD AND tmt.TBL_CD = TSSH.TBL_CD AND TSSH.STORE_CD = tssd.STORE_CD AND TSSH.SALE_DATE = tssd.SALE_DATE AND TSSH.POS_NO = tssd.POS_NO AND TSSH.BILL_NO = tssd.BILL_NO) AS TBL_CD , TO_CHAR(TO_DATE(tssd.BILL_DT, 'YYYYMMDDHH24MISS'), 'yyyy-MM-dd HH:mm:ss') AS BILL_DT , SUM(tssd.SALE_AMT) AS TOT_SALE_AMT , SUM(tssd.DC_AMT) AS TOT_DC_AMT , SUM(tssd.REAL_SALE_AMT) AS REAL_SALE_AMT FROM TB_SL_SALE_DTL tssd WHERE 1=1 AND tssd.HQ_OFFICE_CD = #{hqOfficeCd}
AND tssd.STORE_CD = #{storeCd}
AND tssd.SALE_DATE >= #{startDate}
AND tssd.SALE_DATE <= #{endDate}
AND tssd.ORDER_EMP_NO = #{empNo} GROUP BY SALE_DATE, STORE_CD, POS_NO, BILL_NO, SALE_YN, BILL_DT ORDER BY tssd.SALE_DATE DESC, tssd.POS_NO, tssd.BILL_NO

/* USE : EmpDayMapper.getOrderEmpDayList */

SELECT 	TO_CHAR(TO_DATE(A.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE
, 		A.STORE_CNT
, 		TO_CHAR(TO_DATE(A.SALE_DATE, 'YYYYMMDD'),'DY') YOIL
, 		SUM(A.REAL_SALE_AMT_TOT) AS REAL_SALE_AMT_TOT
, 		SUM(A.TOT_BILL_CNT) AS TOT_BILL_CNT
		${sQuery1}

FROM
(
SELECT tssd.SALE_DATE
	,( SELECT COUNT(TMS.STORE_CD)
	FROM TB_MS_STORE TMS
	WHERE TMS.HQ_OFFICE_CD = #{hqOfficeCd}

		AND TMS.STORE_CD IN

			#{item}


	) AS STORE_CNT
, SUM(tssd.REAL_SALE_AMT) AS REAL_SALE_AMT_TOT
, COUNT(tssd.BILL_NO) AS TOT_BILL_CNT
${sQuery2}
FROM TB_SL_SALE_DTL tssd
WHERE tssd.HQ_OFFICE_CD = #{hqOfficeCd}

	AND tssd.STORE_CD	IN

		#{item}



	AND tssd.SALE_DATE >= #{startDate}


	AND tssd.SALE_DATE <=  #{endDate}

GROUP BY tssd.SALE_DATE, tssd.ORDER_EMP_NO, tssd.STORE_CD
ORDER BY tssd.SALE_DATE ASC
) A
WHERE 1 = 1
GROUP BY A.SALE_DATE, A.STORE_CNT
ORDER BY A.SALE_DATE

/* USE : EmpDayMapper.getOrderEmpDayExcelList */
SELECT TO_CHAR(TO_DATE(A.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE
, A.STORE_CNT
, TO_CHAR(TO_DATE(A.SALE_DATE, 'YYYYMMDD'),'DY') YOIL
, SUM(A.REAL_SALE_AMT_TOT) AS REAL_SALE_AMT_TOT
, SUM(A.TOT_BILL_CNT) AS TOT_BILL_CNT
${sQuery1}
FROM
(
SELECT TSDE.SALE_DATE
,( SELECT COUNT(TMS.STORE_CD)
FROM TB_MS_STORE TMS
WHERE TMS.HQ_OFFICE_CD = #{hqOfficeCd}

	AND TMS.STORE_CD	IN

		#{item}


) AS STORE_CNT
, SUM(TSDE.REAL_SALE_AMT) AS REAL_SALE_AMT_TOT
, SUM(TSDE.BILL_CNT) AS TOT_BILL_CNT
${sQuery2}
FROM TB_SL_DAILY_EMPLOYEE TSDE
WHERE TSDE.HQ_OFFICE_CD = #{hqOfficeCd}

	AND TSDE.STORE_CD	IN

		#{item}



	AND TSDE.SALE_DATE >= #{startDate}


	AND TSDE.SALE_DATE <=  #{endDate}

GROUP BY TSDE.SALE_DATE, TSDE.ORDER_EMP_NO, TSDE.STORE_CD
ORDER BY TSDE.SALE_DATE ASC
) A
WHERE 1 = 1
GROUP BY A.SALE_DATE, A.STORE_CNT
ORDER BY A.SALE_DATE

/* USE : OrderEmpMapper.getEmpMebList */
SELECT TMS.STORE_CD
, TMS.STORE_NM||'('||TMS.STORE_CD||')' AS STORE_NM
, EMP_NO AS  NMCODE_CD
, (EMP_NM||'('||EMP_NO||')') AS  NMCODE_NM
FROM TB_MS_EMPLOYEE TME
, TB_MS_STORE TMS
WHERE TME.STORE_CD = TMS.STORE_CD
AND TMS.HQ_OFFICE_CD = #{hqOfficeCd}

	AND TME.STORE_CD IN

		#{item}



	AND EXISTS (SELECT * FROM TB_SL_SALE_HDR Z
	WHERE Z.store_cd = TME.STORE_CD
	AND Z.EMP_NO = TME.EMP_NO

		AND Z.STORE_CD IN

			#{item}



		AND Z.SALE_DATE >= #{startDate}


		AND Z.SALE_DATE <=   #{endDate}

	)

ORDER BY TMS.STORE_CD
