/* USE : PayTemporaryMapper.getPayTemporaryList */
SELECT tms.BRANCH_CD , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tsspt.STORE_CD , tms.STORE_NM , tms.HQ_BRAND_CD AS BRAND , tmsi.MOMS_TEAM , tmsi.MOMS_AC_SHOP , #{startDate} AS START_DATE , #{endDate} AS END_DATE
, TO_CHAR(TO_DATE(tsspt.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE
, COUNT(*) AS SALE_CNT , SUM(SALE_AMT) AS SALE_AMT
FROM TB_SL_SALE_PAY_TEMPORARY tsspt , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tsspt.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsspt.STORE_CD IN
#{item}
AND tsspt.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tsspt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsspt.STORE_CD = tms.STORE_CD AND tsspt.STORE_CD = tmsi.STORE_CD(+)
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
GROUP BY tms.BRANCH_CD , tsspt.STORE_CD , tms.STORE_NM , tms.HQ_BRAND_CD , tmsi.MOMS_TEAM , tmsi.MOMS_AC_SHOP
, tsspt.SALE_DATE

/* USE : PayTemporaryMapper.getPayTemporaryExcelList */
SELECT  tms.BRANCH_CD
,		(SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM
,		tsspt.STORE_CD
,		tms.STORE_NM
,       tms.HQ_BRAND_CD AS BRAND
,       tmsi.MOMS_TEAM
,       tmsi.MOMS_AC_SHOP

    ,       TO_CHAR(TO_DATE(tsspt.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE

,		COUNT(*) AS SALE_CNT
,		SUM(SALE_AMT) AS SALE_AMT
FROM    TB_SL_SALE_PAY_TEMPORARY tsspt
,       TB_MS_STORE tms
,       TB_MS_STORE_INFO tmsi
WHERE   tsspt.HQ_OFFICE_CD  = #{hqOfficeCd}

    AND tsspt.STORE_CD IN

        #{item}


AND     tsspt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND     tsspt.HQ_OFFICE_CD = tms.HQ_OFFICE_CD
AND     tsspt.STORE_CD = tms.STORE_CD
AND     tsspt.STORE_CD = tmsi.STORE_CD(+)

    AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때



        -- 매장브랜드 전체일때
        AND tms.HQ_BRAND_CD IN

            #{item}




    AND tmsi.MOMS_TEAM = #{momsTeam}


    AND tmsi.MOMS_AC_SHOP = #{momsAcShop}


    AND tmsi.MOMS_AREA_FG = #{momsAreaFg}


    AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}


    AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}


    AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}


    AND tms.BRANCH_CD = #{branchCd}

GROUP
BY      tms.BRANCH_CD
,		tsspt.STORE_CD
,		tms.STORE_NM
,       tms.HQ_BRAND_CD
,       tmsi.MOMS_TEAM
,       tmsi.MOMS_AC_SHOP

,       tsspt.SALE_DATE

/* USE : PayTemporaryMapper.getPayTemporaryDtlList */
SELECT TO_CHAR(TO_DATE(tsspt.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , COUNT(*) AS SALE_CNT , SUM(tsspt.SALE_AMT) AS SALE_AMT FROM TB_SL_SALE_PAY_TEMPORARY tsspt WHERE tsspt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsspt.STORE_CD = #{storeCd} AND tsspt.SALE_DATE BETWEEN #{startDate} AND #{endDate} GROUP BY tsspt.SALE_DATE ORDER BY tsspt.SALE_DATE
SELECT TO_CHAR(TO_DATE(tsspt.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , tsspt.POS_NO , tsspt.BILL_NO , tsspt.LINE_NO , tsspt.LINE_SEQ_NO , DECODE(tsspt.SALE_YN,'Y','매출','반품') AS SALE_YN , tsspt.SALE_AMT FROM TB_SL_SALE_PAY_TEMPORARY tsspt WHERE tsspt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsspt.STORE_CD = #{storeCd} AND tsspt.SALE_DATE = #{saleDate} ORDER BY tsspt.SALE_DATE
