, TO_CHAR(TO_DATE(tsspt.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE
, SUM(CASE WHEN tsspt.SALE_YN = 'Y' THEN 1 WHEN tsspt.SALE_YN = 'N' THEN -1 ELSE 0 END) AS SALE_CNT , SUM(tsspt.SALE_AMT) AS SALE_AMT FROM TB_SL_SALE_PAY_TEMPORARY tsspt , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tsspt.HQ_OFFICE_CD = #{hqOfficeCd}
