/* USE : PayTemporaryMapper.getPayTemporaryDtlList */
SELECT tsspt.STORE_CD , tms.STORE_NM , TO_CHAR(TO_DATE(tsspt.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , tsspt.POS_NO , tsspt.BILL_NO , tsspt.LINE_NO , tsspt.LINE_SEQ_NO , DECODE(tsspt.SALE_YN,'Y','매출','반품') AS SALE_YN , (CASE WHEN tsspt.SALE_YN = 'Y' THEN 1 WHEN tsspt.SALE_YN = 'N' THEN -1 ELSE 0 END) AS SALE_CNT , tsspt.SALE_AMT FROM TB_SL_SALE_PAY_TEMPORARY tsspt , TB_MS_STORE tms WHERE tsspt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsspt.STORE_CD = #{storeCd} AND tsspt.SALE_DATE BETWEEN #{startDate} AND #{endDate} AND tsspt.TEMPORARY_PAY_CD = '98' AND tsspt.STORE_CD = tms.STORE_CD ORDER BY tsspt.STORE_CD, tsspt.SALE_DATE, tsspt.POS_NO, tsspt.BILL_NO, tsspt.LINE_NO, tsspt.LINE_SEQ_NO
SELECT TO_CHAR(TO_DATE(tsspt.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE , tsspt.POS_NO , tsspt.BILL_NO , tsspt.LINE_NO , tsspt.LINE_SEQ_NO , DECODE(tsspt.SALE_YN,'Y','매출','반품') AS SALE_YN , (CASE WHEN tsspt.SALE_YN = 'Y' THEN 1 WHEN tsspt.SALE_YN = 'N' THEN -1 ELSE 0 END) AS SALE_CNT , tsspt.SALE_AMT FROM TB_SL_SALE_PAY_TEMPORARY tsspt WHERE tsspt.HQ_OFFICE_CD = #{hqOfficeCd} AND tsspt.STORE_CD = #{storeCd} AND tsspt.SALE_DATE = #{saleDate} AND tsspt.TEMPORARY_PAY_CD = '98' ORDER BY tsspt.SALE_DATE, tsspt.POS_NO, tsspt.BILL_NO, tsspt.LINE_NO, tsspt.LINE_SEQ_NO

/* PayTemporaryMapper.getPayTemporaryGiftList */

SELECT
tsspt.STORE_CD,
tms.STORE_NM,
TO_CHAR(TO_DATE(tsspt.SALE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS SALE_DATE,
tsspt.POS_NO,
tsspt.BILL_NO,
tsspt.LINE_NO,
tsspt.LINE_SEQ_NO,
tsspt.SALE_YN,
(CASE WHEN tsspt.SALE_YN = 'Y' THEN 1 WHEN tsspt.SALE_YN = 'N' THEN -1 ELSE 0 END) AS SALE_CNT,
tsspt.SALE_AMT,
tmsi.MOMS_TEAM,
tmsi.MOMS_AC_SHOP,
tmsi.MOMS_AREA_FG,
tmsi.MOMS_COMMERCIAL,
tmsi.MOMS_SHOP_TYPE,
tmsi.MOMS_STORE_MANAGE_TYPE,
tms.BRANCH_CD,
tmsi.MOMS_STORE_FG_01,
tmsi.MOMS_STORE_FG_02,
tmsi.MOMS_STORE_FG_03,
tmsi.MOMS_STORE_FG_04,
tmsi.MOMS_STORE_FG_05

FROM TB_SL_SALE_PAY_TEMPORARY tsspt,
TB_MS_STORE tms,
TB_MS_STORE_INFO tmsi
WHERE 1=1
AND tsspt.HQ_OFFICE_CD = #{hqOfficeCd}

    AND tsspt.STORE_CD IN (${storeCdQuery})

AND tsspt.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsspt.TEMPORARY_PAY_CD = '98'
AND tms.HQ_OFFICE_CD = tsspt.HQ_OFFICE_CD
AND tms.STORE_CD = tsspt.STORE_CD
AND tmsi.STORE_CD (+)= tsspt.STORE_CD

    AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때



        -- 매장브랜드 전체일때
        AND tms.HQ_BRAND_CD IN

            #{item}




    AND tmsi.MOMS_TEAM = #{momsTeam}


    AND tmsi.MOMS_AC_SHOP = #{momsAcShop}


    AND tmsi.MOMS_AREA_FG = #{momsAreaFg}


    AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}


    AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}


    AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}


    AND tms.BRANCH_CD = #{branchCd}


    AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}


    AND tmsi.MOMS_STORE_FG_02 = #{momsStoreFg02}


    AND tmsi.MOMS_STORE_FG_03 = #{momsStoreFg03}


    AND tmsi.MOMS_STORE_FG_04 = #{momsStoreFg04}


    AND tmsi.MOMS_STORE_FG_05 = #{momsStoreFg05}

ORDER BY tsspt.STORE_CD, tsspt.SALE_DATE, tsspt.POS_NO, tsspt.BILL_NO, tsspt.LINE_NO, tsspt.LINE_SEQ_NO

