SELECT A.DAY_NAME , A.DAY_ORDER , A.SALE_STORE_CNT , A.TOT_SALE_AMT , A.TOT_DC_AMT , A.TOT_REAL_SALE_AMT , A.TOT_SALE_CNT , B.*
FROM ( SELECT TO_CHAR(TO_DATE(TSDP.SALE_DATE, 'YYYY-MM-DD'),'DY','NLS_DATE_LANGUAGE=korean') AS DAY_NAME , CASE TO_CHAR(TO_DATE(TSDP.SALE_DATE, 'YYYY-MM-DD'),'DY','NLS_DATE_LANGUAGE=korean') WHEN '월' THEN 1 WHEN '화' THEN 2 WHEN '수' THEN 3 WHEN '목' THEN 4 WHEN '금' THEN 5 WHEN '토' THEN 6 ELSE 0 END AS DAY_ORDER , COUNT(DISTINCT TSDP.STORE_CD) AS SALE_STORE_CNT , SUM(TSDP.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(TSDP.TOT_DC_AMT) AS TOT_DC_AMT , SUM(TSDP.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT , SUM(TSDP.SALE_CNT) AS TOT_SALE_CNT FROM TB_SL_DAILY_POS TSDP WHERE 1 = 1 AND TSDP.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSDP.STORE_CD||'||'||TSDP.POS_NO IN
#{item}
AND TSDP.SALE_DATE >= #{startDate}
AND TSDP.SALE_DATE <= #{endDate}
GROUP BY TO_CHAR(TO_DATE(TSDP.SALE_DATE, 'YYYY-MM-DD'),'DY','NLS_DATE_LANGUAGE=korean') ) A, ( WITH T AS ( SELECT DISTINCT TSDP.STORE_CD || '||' || TSDP.POS_NO AS STORE_CD , TO_CHAR(TO_DATE(TSDP.SALE_DATE, 'YYYY-MM-DD'),'DY','NLS_DATE_LANGUAGE=korean') AS DAY_NAME_TEMP , TSDP.TOT_SALE_AMT AS SALE_AMT , TSDP.TOT_DC_AMT AS DC_AMT , TSDP.REAL_SALE_AMT AS REAL_SALE_AMT , TSDP.SALE_CNT AS SALE_CNT FROM TB_SL_DAILY_POS TSDP , TB_SL_DAILY_POS_PROD TSDPP WHERE TSDP.HQ_OFFICE_CD = TSDPP.HQ_OFFICE_CD AND TSDP.HQ_BRAND_CD = TSDPP.HQ_BRAND_CD AND TSDP.STORE_CD = TSDPP.STORE_CD AND TSDP.SALE_DATE = TSDPP.SALE_DATE AND TSDP.POS_NO = TSDPP.POS_NO AND TSDP.HQ_OFFICE_CD = #{hqOfficeCd}
AND TSDP.STORE_CD||'||'||TSDP.POS_NO IN
#{item}
AND TSDP.SALE_DATE >= #{startDate}
AND TSDP.SALE_DATE <= #{endDate}
) SELECT * FROM T PIVOT ( MAX(SALE_AMT) AS SALE_AMT, MAX(DC_AMT) AS DC_AMT, MAX(REAL_SALE_AMT) AS REAL_SALE_AMT, MAX(SALE_CNT) AS SALE_CNT FOR STORE_CD IN (
'${item}'
) ) ) B WHERE A.DAY_NAME = B.DAY_NAME_TEMP ORDER BY A.DAY_ORDER

    	SELECT	DISTINCT TSDP.STORE_CD || '||' || TSDP.POS_NO AS STORE_CD
    	FROM	TB_SL_DAILY_POS TSDP
    			, TB_SL_DAILY_POS_PROD TSDPP
    	WHERE	TSDP.HQ_OFFICE_CD = TSDPP.HQ_OFFICE_CD
    			AND TSDP.HQ_BRAND_CD = TSDPP.HQ_BRAND_CD
    			AND TSDP.STORE_CD = TSDPP.STORE_CD
    			AND TSDP.SALE_DATE = TSDPP.SALE_DATE
    			AND TSDP.POS_NO = TSDPP.POS_NO
    			AND TSDP.HQ_OFFICE_CD = #{hqOfficeCd}

               	AND TSDP.STORE_CD IN

                   	#{item}



               	AND TSDP.STORE_CD||'||'||TSDP.POS_NO IN

                   	#{item}



				AND TSDP.SALE_DATE >= #{startDate}


				AND TSDP.SALE_DATE <=  #{endDate}

		ORDER BY TSDP.STORE_CD||'||'||TSDP.POS_NO

SELECT TMP.STORE_CD||'||'||TMP.POS_NO AS POS_CD , TMP.POS_NM AS POS_NM , (SELECT STORE_NM FROM TB_MS_STORE WHERE STORE_CD = TMP.STORE_CD) AS STORE_NM FROM TB_MS_POS TMP , TB_MS_STORE TMS WHERE TMP.STORE_CD = TMS.STORE_CD
AND TMS.HQ_OFFICE_CD = #{hqOfficeCd}
AND TMP.STORE_CD IN
#{item}
ORDER BY TMP.STORE_CD||'||'||TMP.POS_NO
