mapper.select:
SELECT A.PROD_CD , A.PROD_NM , A.SALE_STORE_CNT , A.TOT_SALE_AMT , A.TOT_DC_AMT , A.TOT_REAL_SALE_AMT , A.TOT_SALE_CNT , B.*
FROM ( SELECT TSDPP.PROD_CD AS PROD_CD , TMP.PROD_NM AS PROD_NM , COUNT(DISTINCT TSDP.STORE_CD) AS SALE_STORE_CNT , SUM(TSDP.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(TSDP.TOT_DC_AMT) AS TOT_DC_AMT , SUM(TSDP.REAL_SALE_AMT) AS TOT_REAL_SALE_AMT , SUM(TSDP.SALE_CNT) AS TOT_SALE_CNT FROM TB_SL_DAILY_POS TSDP , TB_SL_DAILY_POS_PROD TSDPP , TB_MS_PRODUCT TMP WHERE TSDP.HQ_OFFICE_CD = TSDPP.HQ_OFFICE_CD AND TSDP.HQ_BRAND_CD = TSDPP.HQ_BRAND_CD AND TSDP.STORE_CD = TSDPP.STORE_CD AND TSDP.SALE_DATE = TSDPP.SALE_DATE AND TSDP.POS_NO = TSDPP.POS_NO AND TSDPP.HQ_BRAND_CD = TMP.HQ_BRAND_CD AND TSDPP.STORE_CD = TMP.STORE_CD AND TSDPP.PROD_CD = TMP.PROD_CD
AND TSDP.STORE_CD||'||'||TSDP.POS_NO IN
#{item}
AND TSDP.SALE_DATE >= #{startDate}
AND TSDP.SALE_DATE <= #{endDate}
GROUP BY TSDPP.PROD_CD , TMP.PROD_NM ) A, ( WITH T AS ( SELECT DISTINCT TSDP.STORE_CD || '||' || TSDP.POS_NO AS STORE_CD , TSDPP.PROD_CD AS PROD_CD_TEMP , TSDP.TOT_SALE_AMT AS SALE_AMT , TSDP.TOT_DC_AMT AS DC_AMT , TSDP.REAL_SALE_AMT AS REAL_SALE_AMT , TSDP.SALE_CNT AS SALE_CNT FROM TB_SL_DAILY_POS TSDP , TB_SL_DAILY_POS_PROD TSDPP WHERE TSDP.HQ_OFFICE_CD = TSDPP.HQ_OFFICE_CD AND TSDP.HQ_BRAND_CD = TSDPP.HQ_BRAND_CD AND TSDP.STORE_CD = TSDPP.STORE_CD AND TSDP.SALE_DATE = TSDPP.SALE_DATE AND TSDP.POS_NO = TSDPP.POS_NO
AND TSDP.STORE_CD||'||'||TSDP.POS_NO IN
#{item}
AND TSDP.SALE_DATE >= #{startDate}
AND TSDP.SALE_DATE <= #{endDate}
) SELECT * FROM T PIVOT ( MAX(SALE_AMT) AS SALE_AMT, MAX(DC_AMT) AS DC_AMT, MAX(REAL_SALE_AMT) AS REAL_SALE_AMT, MAX(SALE_CNT) AS SALE_CNT FOR STORE_CD IN (
'${item}'
) ) ) B WHERE A.PROD_CD = B.PROD_CD_TEMP ORDER BY A.PROD_CD

SELECT DISTINCT TSDP.STORE_CD || '||' || TSDP.POS_NO AS STORE_CD FROM TB_SL_DAILY_POS TSDP , TB_SL_DAILY_POS_PROD TSDPP WHERE TSDP.HQ_OFFICE_CD = TSDPP.HQ_OFFICE_CD AND TSDP.HQ_BRAND_CD = TSDPP.HQ_BRAND_CD AND TSDP.STORE_CD = TSDPP.STORE_CD AND TSDP.SALE_DATE = TSDPP.SALE_DATE AND TSDP.POS_NO = TSDPP.POS_NO
AND TSDP.STORE_CD IN
#{item}
AND TSDP.STORE_CD||'||'||TSDP.POS_NO IN
#{item}
AND TSDP.SALE_DATE >= #{startDate}
AND TSDP.SALE_DATE <= #{endDate}
ORDER BY TSDP.STORE_CD||'||'||TSDP.POS_NO
