SELECT TMP.STORE_CD||'||'||TMP.POS_NO AS POS_CD , TMP.POS_NM AS POS_NM , (SELECT STORE_NM FROM TB_MS_STORE WHERE STORE_CD = TMP.STORE_CD) AS STORE_NM FROM TB_MS_POS TMP , TB_MS_STORE TMS WHERE TMP.STORE_CD = TMS.STORE_CD
AND TMS.HQ_OFFICE_CD = #{hqOfficeCd}
AND TMP.STORE_CD IN
#{item}
