mapper.select:
/* USE : ProdRankMapper.getProdRankList */
SELECT TMP.PROD_CLASS_CD , TMPC.T_PROD_CLASS_CD , TMPCD.PROD_CLASS_NM , TMP.PROD_CD , TMP.PROD_NM , SUM(TSDP.TOT_SALE_QTY) AS TOT_SALE_QTY , SUM(TSDP.TOT_SALE_AMT) AS TOT_SALE_AMT , SUM(TSDP.TOT_DC_AMT) AS TOT_DC_AMT , SUM(TSDP.REAL_SALE_AMT) AS REAL_SALE_AMT
FROM TB_SL_DAILY_PROD TSDP , TB_MS_PRODUCT TMP , (SELECT DISTINCT PROD_CLASS_CD , CONNECT_BY_ROOT PROD_CLASS_CD AS T_PROD_CLASS_CD FROM (SELECT PROD_CLASS_CD , P_PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD IN
#{item}
) START WITH P_PROD_CLASS_CD ='00000' CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD ) TMPC , ( SELECT DISTINCT PROD_CLASS_CD , PROD_CLASS_NM FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD IN
#{item}
) TMPCD WHERE TSDP.STORE_CD = TMP.STORE_CD AND TSDP.PROD_CD = TMP.PROD_CD AND TMP.PROD_CLASS_CD = TMPC.PROD_CLASS_CD AND TMPC.T_PROD_CLASS_CD = TMPCD.PROD_CLASS_CD
AND TSDP.STORE_CD IN
#{item}
AND TSDP.SALE_DATE >= #{startDate}
AND TSDP.SALE_DATE <= #{endDate}
AND TMP.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
AND TMP.PROD_CD = #{prodCd}
AND TMP.PROD_NM LIKE '%'||#{prodNm}||'%'
GROUP BY TMP.PROD_CLASS_CD, TMPC.T_PROD_CLASS_CD, TMPCD.PROD_CLASS_NM , TMP.PROD_CD, TMP.PROD_NM ORDER BY TMP.PROD_CLASS_CD DESC
