mapper.select:
/* USE : ProdPayFgMapper.getProdPayFgList */
SELECT PATH_NM, PROD_CD, PROD_NM, BARCD_CD, SUM(TOT_SALE_QTY) AS TOT_SALE_QTY, SUM(TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(TOT_DC_AMT) AS TOT_DC_AMT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(TOT_GA_AMT) AS TOT_GA_AMT, SUM(TOT_VAT_AMT) AS TOT_VAT_AMT, SUM(TOT_TIP_AMT) AS TOT_TIP_AMT, SUM(TOT_ETC_AMT) AS TOT_ETC_AMT,
NVL(SUM(PAY${item}), 0) AS TOT_PAY_AMT,
NVL(SUM(PAY${item}), 0) AS PAY${item}
FROM ( SELECT FN_GET_PROD_CLASS_CD_NM_STR(tmp.STORE_CD, tmp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM, --NM_STR tsdp.PROD_CD, tmp.PROD_NM, tmpb.BARCD_CD, SUM(tsdp.TOT_SALE_QTY) AS TOT_SALE_QTY, SUM(tsdp.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsdp.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsdp.REAL_SALE_AMT) AS REAL_SALE_AMT, (SUM(tsdp.REAL_SALE_AMT) - SUM(tsdp.TOT_VAT_AMT)) AS TOT_GA_AMT, SUM(tsdp.TOT_VAT_AMT) AS TOT_VAT_AMT, SUM(tsdp.TOT_TIP_AMT) AS TOT_TIP_AMT, SUM(tsdp.TOT_ETC_AMT) AS TOT_ETC_AMT,
SUM(tsdpp.PAY${item}) AS PAY${item}
FROM TB_SL_DAILY_PROD tsdp, TB_MS_PRODUCT tmp, TB_MS_PRODUCT_BARCD tmpb, ( SELECT HQ_OFFICE_CD, HQ_BRAND_CD, STORE_CD, PROD_CD, SALE_DATE,
SUM(PAY${item}) AS PAY${item}
FROM ( SELECT HQ_OFFICE_CD, HQ_BRAND_CD, STORE_CD, PROD_CD, SALE_DATE, PAY_AMT, PAY_CD FROM TB_SL_DAILY_PROD_PAY WHERE 1=1 AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD IN
#{item}
AND SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND PROD_CD LIKE '%'|| #{prodCd} ||'%'
) PIVOT ( SUM(PAY_AMT) FOR PAY_CD IN (${pivotPayCol}) ) GROUP BY HQ_OFFICE_CD, HQ_BRAND_CD, STORE_CD, PROD_CD, SALE_DATE ) tsdpp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdp.PROD_CD LIKE '%'|| #{prodCd} ||'%'
AND tmp.PROD_NM LIKE '%'|| #{prodNm} ||'%'
AND tmp.STORE_CD = tsdp.STORE_CD AND tmp.PROD_CD = tsdp.PROD_CD AND tmpb.STORE_CD (+)= tmp.STORE_CD AND tmpb.PROD_CD (+)= tmp.PROD_CD AND tsdpp.HQ_OFFICE_CD (+)= tsdp.HQ_OFFICE_CD AND tsdpp.HQ_BRAND_CD (+)= tsdp.HQ_BRAND_CD AND tsdpp.STORE_CD (+)= tsdp.STORE_CD AND tsdpp.PROD_CD (+)= tsdp.PROD_CD AND tsdpp.SALE_DATE (+)= tsdp.SALE_DATE GROUP BY tsdp.PROD_CD, tmp.PROD_NM, tmpb.BARCD_CD, tmp.STORE_CD, tmp.PROD_CLASS_CD ) GROUP BY PROD_CD, PROD_NM, BARCD_CD, PATH_NM ORDER BY PROD_CD

/* USE : ProdPayFgMapper.getProdPayFgExcelList */ SELECT PATH_NM, PROD_CD, PROD_NM, BARCD_CD, SUM(TOT_SALE_QTY) AS TOT_SALE_QTY, SUM(TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(TOT_DC_AMT) AS TOT_DC_AMT, SUM(REAL_SALE_AMT) AS REAL_SALE_AMT, SUM(TOT_GA_AMT) AS TOT_GA_AMT, SUM(TOT_VAT_AMT) AS TOT_VAT_AMT, SUM(TOT_TIP_AMT) AS TOT_TIP_AMT, SUM(TOT_ETC_AMT) AS TOT_ETC_AMT,
NVL(SUM(PAY${item}), 0) AS TOT_PAY_AMT,
NVL(SUM(PAY${item}), 0) AS PAY${item}
FROM ( SELECT FN_GET_PROD_CLASS_CD_NM_STR(tmp.STORE_CD, tmp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM, --NM_STR tsdp.PROD_CD, tmp.PROD_NM, tmpb.BARCD_CD, SUM(tsdp.TOT_SALE_QTY) AS TOT_SALE_QTY, SUM(tsdp.TOT_SALE_AMT) AS TOT_SALE_AMT, SUM(tsdp.TOT_DC_AMT) AS TOT_DC_AMT, SUM(tsdp.REAL_SALE_AMT) AS REAL_SALE_AMT, (SUM(tsdp.REAL_SALE_AMT) - SUM(tsdp.TOT_VAT_AMT)) AS TOT_GA_AMT, SUM(tsdp.TOT_VAT_AMT) AS TOT_VAT_AMT, SUM(tsdp.TOT_TIP_AMT) AS TOT_TIP_AMT, SUM(tsdp.TOT_ETC_AMT) AS TOT_ETC_AMT,
SUM(tsdpp.PAY${item}) AS PAY${item}
FROM TB_SL_DAILY_PROD tsdp, TB_MS_PRODUCT tmp, TB_MS_PRODUCT_BARCD tmpb, ( SELECT HQ_OFFICE_CD, HQ_BRAND_CD, STORE_CD, PROD_CD, SALE_DATE,
SUM(PAY${item}) AS PAY${item}
FROM ( SELECT HQ_OFFICE_CD, HQ_BRAND_CD, STORE_CD, PROD_CD, SALE_DATE, PAY_AMT, PAY_CD FROM TB_SL_DAILY_PROD_PAY WHERE 1=1 AND HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD IN
#{item}
AND SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND PROD_CD LIKE '%'|| #{prodCd} ||'%'
) PIVOT ( SUM(PAY_AMT) FOR PAY_CD IN (${pivotPayCol}) ) GROUP BY HQ_OFFICE_CD, HQ_BRAND_CD, STORE_CD, PROD_CD, SALE_DATE ) tsdpp WHERE 1=1 AND tsdp.HQ_OFFICE_CD = #{hqOfficeCd}
AND tsdp.STORE_CD IN
#{item}
AND tsdp.SALE_DATE BETWEEN #{startDate} AND #{endDate}
AND tsdp.PROD_CD LIKE '%'|| #{prodCd} ||'%'
AND tmp.PROD_NM LIKE '%'|| #{prodNm} ||'%'
AND tmp.STORE_CD = tsdp.STORE_CD AND tmp.PROD_CD = tsdp.PROD_CD AND tmpb.STORE_CD (+)= tmp.STORE_CD AND tmpb.PROD_CD (+)= tmp.PROD_CD AND tsdpp.HQ_OFFICE_CD (+)= tsdp.HQ_OFFICE_CD AND tsdpp.HQ_BRAND_CD (+)= tsdp.HQ_BRAND_CD AND tsdpp.STORE_CD (+)= tsdp.STORE_CD AND tsdpp.PROD_CD (+)= tsdp.PROD_CD AND tsdpp.SALE_DATE (+)= tsdp.SALE_DATE GROUP BY tsdp.PROD_CD, tmp.PROD_NM, tmpb.BARCD_CD, tmp.STORE_CD, tmp.PROD_CLASS_CD ) GROUP BY PROD_CD, PROD_NM, BARCD_CD, PATH_NM ORDER BY PROD_CD
